INSERT INTO books (name, description,  author_name, pages, year, imgUrl, clicks, view)
VALUES
    ("Останній з Могикан", "Джеймса Фенімора Купера справді можна назвати батьком американського пригодницького роману. Завдяки йому в літературу увійшли дивовижні і своєрідні реалії далекого континенту, населеного безжальними і відважними індіанцями, вони відчайдушно і безнадійно захищають рідні землі від блідолицього людини. Яскраві, незвичайні і захоплюючі романи Джеймса Фенімора Купера користувалися величезним успіхом по всьому світу, в тому числі і в Росії, де їх активно перекладали вже в 1840-х роках. Одним з найвідоміших творів є роман 'Останній з могікан', який і пропонується читачам в цьому виданні.", "Джеймс Купер", 224, 2002, "/images/40bc60e4-1a50-4008-90a1-c44a475163ed.jpg", 0,  0),
    ("Обрані твори", "До збірки обраних творів Михайла Юрійовича Лермонтова увійшли вірші 1828 - 1841 років, поеми, драма 'Маскарад', прозові твори - 'Княгиня Лиговска', 'Герой нашого часу', 'Панорама Москви'.", "М.Ю. Лермонтов", 383, 1984, "/images/b829dee0-69ba-4a23-a459-80a3baf0c07d.jpg", 0,  0),
    ("Пульт Гвенді", "Багато моторошних історій сталося в містечку Касл-Рок, що в штаті Мейн. Але одну з них ще нікому не розповідали… Дотепер.Юна Ґвенді потерпає від зайвої ваги. Щодня вздовж урвища вона видирається на гору Сходами самогубців, щоб схуднути, і, відсапуючись, бачить внизу дітлахів на ігровому майданчику. Та одного разу звичну картину порушить незнайомець у чорному капелюсі. Він подарує дівчинці загадковий пристрій, який після натискання кнопки видає шоколадне звірятко, що втамовує голод, і срібні монети, за які колекціонери ладні віддати цілі статки. Та є інші кнопки цього пульту, які Ґвенді не наважиться використати. Або?..", "Стівен Кінг, Річард Чізмар", 156, 2017, "/images/7948742f-72e6-4c8a-8bc1-95a4d68d1449.jpg", 0,  0),
    ("Программирование в алгоритмах", "Мистецтво програмування представлено у вигляді навчального курсу, який розкриває секрети найбільш популярних алгоритмів. Висвітлено такі питання, як комбінаторні алгоритми, перебирання, алгоритми на графах, алгоритми обчислювальної геометрії. Наводяться обрані олімпіадні задачі з програмування з вказівками до вирішення. Практичні рекомендації з тестування програм є необхідним доповненням курсу.", "С. Окулов", 341, 2004, "/images/f12976f3-c7d1-45a0-9539-2a2944430267.jpg", 0,  0),
    ("Ководство", "Третє видання виходить з новими параграфами і в оновленому макеті.", "Артемій Лебедєв", 452, 2011, "/images/bffd33d6-c5e7-4ca7-928f-00084d1aeeb9.jpg", 0,  0),
    ("Философия Java", "Повна нова версія книги про основні проблеми програмування: чому вони виникають і який підхід використовує Java в їх вирішенні.", "Брюс Еккель", 1168, 2017, "/images/e0f1ab32-d53e-4050-9461-9d402d6f6894.jpg", 0,  0),
    ("Мёртвые души", "Легендарний твір Миколи Васильовича Гоголя 'Мертві душі' - унікальний роман, який став для російської літератури своєрідним еталоном іронічної прози. Твір вже не одне покоління притягує до сторінок книги шанувальників і критиків.", "Микола Гоголь", 368, 1985, "/images/d8c2e56e-800d-427d-abc9-30ca59a21dc5.jpg", 0,  0),
    ("Harry Potter and the Philosopher's stone", "Сирота на ім'я Гаррі живе під сходами у будинку дядька тітоньски Дурслі. Він поки що не знає, як потрапити на платформу дев'ять і три чверті, користуватися чарівною паличкою, та літати на мітлі. Але одного разу до нього прилетить сова із загаднковим листом, який запрошує його до навчання у Школі Чарів та Чаклунства, і з того дня його життя докорінно зміниться...", "Джоан Роулінг", 254, 2014, "/images/5faeac26-8086-4640-bb0e-2e1553745383.jpg", 0,  0),
    ("Его - це ворог", "Чому реалізм, а не егоїзм — це сила, що рухає вас уперед. Ми звикли звинувачувати інших у власних проблемах і поразках. Але часто найзапекліший ворог успіху живе всередині нас — наше его. Це зверхність, нездорова віра у власну важливість, зацикленість на собі, які не дають нам рухатися вперед, опановувати нові навички, креативити чи налагоджувати відкриті стосунки з людьми. Утім его можна підкорити.", "Раян Голідей", 208, 2019, "/images/d3ae838e-5e8c-4ec8-8bf4-ffd52b50b6ab.jpg", 0,  0),
    ("Garfield classics", "Пригоди рудого кота Гарфілда.", "Джим Девіс", 140, 2004, "/images/710569a5-7cae-4b8f-b327-befc766c29c7.jpg", 0,  0),
    ("Мёртвая зона", "У гостросюжетному романі відомого американського письменника Стівена Кінга неймовірна, фантастична доля героя, що внаслідок тяжкої мозкової травми став екстрасенсом, зображена на тлі реальних політичних подій в Америці 70-х років.", "Стівен Кінг", 416, 1999, "/images/64235e2c-10e4-4b4d-ba53-e135e37b3641.jpg", 0,  0),
    ("Планета людей. Маленький принц", "У книгу включені два твори відомого французького письменника Антуана де Сент-Екзюпері: 'Планета людей' - поетично-прекрасний і по-філософськи заглиблений репортаж військового льотчика і казка 'Маленький принц', яка стверджує красу людських відносин. Твори Сент-Екзюпері написані з великою любов'ю до життя, з наполегливим прагненням зрозуміти свого часу, з почуттям великої відповідальності перед людьми.", "Антуан де Сент-Езюпері", 179, 1970, "/images/d277318b-affb-42df-a71b-35385e218faa.jpg", 0,  0),
    ("Легенды и мифы древней Греции", "Знання основних сюжетів давньогрецької міфології необхідно для будь-якогї культурного людини.", "Микола Кун", 512, 2008, "/images/55613cb2-93df-486e-b119-c5974b6756d2.jpg", 0,  0),
    ("Тигролови", "Про що ж цей роман? Він не тільки про сім'ю Сірків, що заробляють на життя виловом і вбивством тигрів, про молодого революціонера Григорія Многогрішного, про кохання між молодими людьми, на долю яких випало чимало важких випробувань. Насамперед ,,Тигролови'' - це твір про сміливість, відвагу, стальну силу волі людини, яка прагне знайти своє щастя, про жагу до свободи...", "Іван Багряний", 304, 2015, "/images/b378287b-e46f-4bba-a61f-a532ef9bb6d2.jpg", 0,  0),
    ("Programming Language Pragmatics", "Книга Programming Language Pragmatics — найповніший підручник з мов програмування, доступний на сьогодні. Його відзначають його інтегрованою обробкою мовного дизайну та впровадження, з акцентом на основні компроміси, які продовжують рухати розробку програмного забезпечення. Книга надає читачам міцну основу в синтаксисі, семантиці та прагматиці всього спектру мов програмування, від традиційних мов, таких як С, до нових мов, у функціональному та об'єктно-орієнтованому програмуванні.", "Майкл Л. Скотт", 876, 2006, "/images/d32ccc4d-c7c5-44ac-9bff-623589074f33.jpg", 0,  0),
    ("Си++ и компьютерная графика", "Ця книга являє собою навчальний посібник, де описуються основні поняття і методи сучасного практичного програмування, оволодівши якими читач зможе самостійно створювати додатки до відомої російської САПР КОМПАС, до її базовим конструкторським модулів - креслярсько-графічній системі КОМПАС-Графік і системі тривимірного, твердотільного моделювання КОМПАС -ЗО.", "Андрій Богуславский", 352, 2003, "/images/e748171b-f340-40c6-b204-a798425c30cd.jpg", 0,  0),
    ("Відьмак. Час Погорди. Книга 4", "Магія Анджея Сапковського — у його вмінні з ліризмом і сарказмом створити уявний світ, кожен з мешканців якого, кожне місце і кожна подія настільки нагадують нам світ реальний, сучасний, що відірватися від книжок неможливо. Саме тому героїчна сага про відьмака посідає четверте місце за накладами у Польщі.", "Анджей Сапковский", 368, 2014, "/images/156dbcc5-929e-4148-a1d8-21bd73ecfe88.jpg", 0,  0),
    ("Homo Deus Людина Божественна За лаштунками майбутнього", "Протягом минулого століття людству вдалося зробити неможливе: приборкати голод, епідемії і війни. Здається, що в це важко повірити, проте, як пояснює Харарі в своєму фірмовому стилі - досконалому і захопливому - голод, епідемія і війна трансформувалися з незрозумілих і неконтрольованих сил природи у виклики, якими можна керувати. Вперше в історії більше людей помирає від переїдання, ніж від голодування; більше людей помирає від старості, ніж від інфекційних захворювань; і більше людей скоюють самогубство, ніж вбито солдатами, терористами і злочинцями взятими разом. У середнього американця в тисячу разів більше можливостей померти від харчування у МакДоналдсі, ніж бути вбитим Аль Каїдою.", "Ювал Ной Харарі", 512, 2019, "/images/4093500c-fc21-4008-88d1-78a2a95ac630.jpg", 0,  0),
    ("Архив Шерлока Холмса", "Історії про Шерлока Холмса та доктора Джона Уотсона є світовою класикою детективного жанру. Ось уже понад сто років ними зачитуються мільйони читачів в усьому світі.", "Артур Конан Дойл", 286, 2016, "/images/99dcf127-1197-440c-90d6-f948f18217cf.jpg", 0,  0),
    ("Объектно-ориентированное программирование в С++", "Завдяки цій книзі тисячі користувачів опанували технологію об'єктно-орієнтованого програмування в С++. У ній є все: основні принципи мови, готові повномасштабні програми, невеликі приклади, що пояснюють теорію, і безліч корисних іллюстрацій.", "Роберт Лафоре", 928, 2003, "/images/405b4643-b82f-4ae7-87f7-127e988412b5.jpg", 0,  0),
    ("Varulvsnatt", "Книга шведською. Або Нідерландською. Взяв почитати і зрозумів? Розкажи нам!", "Paul Van Loon", 170, 2007, "/images/f8705144-9682-475a-9321-7a46bb3d5099.jpg", 0,  0),
    ("Щоденник слабака", "Дитинство — жахлива пора. І кому це знати краще, як не Ґреґу Гефлі! Середня школа, де вчорашня дрібнота опиняється серед рослявих і недобрих підлітків, які вже й бороду голять, це просто катастрофа якась. Але Греґ не втрачає оптимізму й ділиться з нами своїми пригодами й порадами.", "Джефф Кінні", 224, 2019, "/images/65ad06f5-40bc-47f6-bf1b-3a055d723feb.jpg", 0,  0),
    ("Введение в психоанализ", "Зигмунд Фрейд - знаменитий австрійський психолог і психіатр, основоположник психоаналізу, вчений, чиї відкриття збагатили багато галузей сучасних знань. Його роботи 'Тлумачення сновидінь', 'По той бік принципу насолоди', 'Тотем і табу', 'Я і Воно' викликають інтерес не тільки у фахівців-психологів, а й у самого широкого кола читачів.", "Зигмунд Фрейд", 480, 2015, "/images/9d46ae43-5407-4e88-aaec-46d96ddfb93c.jpg", 0,  0),
    ("Big Money. Принципы первых", "'Хто не хоче, шукає причини. Хто хоче, шукає можливості - фраза мого Батька, головна фраза в моєму вихованні'. Ця книга про людей, які завжди шукають можливості і знаходять їх. Все хороше в цьому Світі придумано людьми, які хотіли заробити. Підприємцями і Бізнесменами. Ця книга про них. Про тих, хто кожен день робить цей світ кращим, розвиваючи свій бізнес, створюючи робочі місця і сплачуючи податки.", "Євгеній Черняк", 256, 2019, "/images/7a483978-7eea-41b2-bc83-91d9c089411d.jpg", 0,  0),
    ("Язык программирования Python", "Авторитетне керівництво і детальний путівник по мові програмування Python призначений для практикуючих програмістів. Книга компактна, націлена на суть справи і написана дуже доступною мовою. Детально обговорюються не тільки ядро ​​мови, а й найбільш важливі частини стандартної бібліотеки Python.", "Девід М. Бізлі", 336, 2000, "/images/7f08afbd-482e-4126-b386-f9d5b8408102.jpg", 0,  0),
    ("Всадник без головы", "Британський письменник Майн Рід став всесвітньо популярний завдяки написаному по-пригодницькі захоплюючого роману 'Вершник без голови', а роман завдяки таланту автора став еталоном пригодницького роману. Сюжетна лінія роману вибудована настільки майстерно, що до кінцевої сторінки утримує читача в напрузі.", "Майн Рід", 320, 1992, "/images/fa3e3a3c-0707-4f93-aaba-5bc655949053.jpg", 0,  0),
    ("Мастер и Маргарита", "'Майстер і Маргарита' - Блискучий шедевр, створений Михайлом Булгаковим, зачаровує містична дьяволіада, що оголює вічні теми любові, боротьби добра зі злом, смерті і безсмертя.", "Михайло Булгаков", 384, 1988, "/images/f7dfa635-ad3d-4e33-8dc6-7873ffccbca2.jpg", 0,  0),
    ("Ревизор", "Сумно усвідомлювати, але реальність підштовхує: такі твори, як п'єса Миколи Васильовича Гоголя Ревізор, завжди будуть актуальні. Чому? Тому що часи змінюються, а звичаї, на жаль, залишаються ті ж. Саме з цієї причини, читаючи Ревізора, що відображає дійсність сто років тому, ми бачимо нашу з вами реальність, з усім багажем глобального хабарництва, свавілля і шахрайства, які свого часу майстерно висміяв класик.", "Микола Гоголь", 352, 1984, "/images/611e2216-5852-408d-a64e-8bc7948a7405.jpg", 0,  0),
    ("Java 8 для чайников", "Java - сучасна об'єктно-орієнтована мова програмування. Програма, написана на Java, здатна виконуватися практично на будь-якому комп'ютері. Знаючи Java, можна створювати потужні мультимедійні додатки для будь-якої платформи.", "Баррі Берд", 400, 2016, "/images/10468984-5986-4ff2-a6f8-270d5aa3b2f2.jpg", 0,  0),
    ("Экстремальное программирование", "Ця книга харизматичних лідерів екстремального програмування — про те, як планувати проєкт розробки програмного забезпечення за технологією XP. В основному вона призначена керівникам — тим, хто повинен складати план робіт, а потім стежити, щоб він відповідав дійсності. Вона буде корисна і програмістам з замовниками, оскільки це дві основні ролі в процесі планування та розробки програмного забезпечення.Екстремальне програмування або XP, eXtreme Programming - гнучка методологія розробки програмного забезпечення. Як і в інших agile-методологій, у неї є особливі інструменти, процеси та ролі. Хоча автор XP не придумав нічого нового, а взяв кращі практики гнучкої розробки та посилив до максимуму. Тому програмування і зветься екстремальним.Автор методики — американський розробник Кент Бек. В кінці 90-х років він керував проєкт Chrysler Comprehensive Compensation System і там вперше застосував практику екстремального програмування. Свій досвід і створену концепцію він описав у книзі Extreme Programming Explained, опублікованій в 1999 році. За нею були випущені інші книги, в яких детально описувалися практики XP. До становлення методології причетні також Уорд Каннінгем, Мартін Фаулер і інші.", "Кент Бек", 220, 2002, "/images/5beea39a-85db-45ee-9a53-0062042214e0.jpg", 0,  0),
    ("Возвращение Шерлока Холмса", "Я машинально озирнувся, щоб подивитися на книжкову шафу у себе за спиною, а коли повернув голову, перед моїм робочим столом стояв і посміхався Шерлок Холмс. Не зводячи з нього очей, я повільно підвівся, мовчки зчекав кілька секунд в цілковитому подиві, після чого, мабуть, перший і останній раз в своєму житті зомлів. Перед очима у мене попливл сірий туман, а коли я прийшов в себе, комір мій був розстебнутий і на губах відчувався терпкий смак бренді. Наді мною з пляшкою в руці схилився Холмс.", "Артур Конан Дойл", 286, 2016, "/images/aaef117d-d846-466c-ade6-eee04aad2258.jpg", 0,  0),
    ("Дракула", "Брем Стокер — автор безлічі книг, які свого часу були дуже популярні, - 'Лігво Білого Хробака', 'Перевал змій', 'Леді в савані'. Але справжнє безсмертя знайшов лише один його роман - 'Дракула'.Ця книга — далеко не перше оповідання про вампірів — стала справжньою класикою жанру, його еталоном і послужила причиною бурхливого сплеску всесвітнього захоплення 'вампірською' темою, що не згасає донині. Стокеру вдалося на основі різних міфів створити свій новий, надзвичайно красивий світ, що тягнеться від Середніх віків до наших днів, від загадкової Трансільванії до затишного Лондона. А головне — створити нового міфічного героя. Героя на всі часи.", "Брем Стокер", 480, 2007, "/images/aa8d3b8b-1892-468e-b66d-1734cea3eef0.jpg", 0,  0),
    ("Затерянный мир", "У 1912 році творець Шерлока Холмса Артур Конан Дойл опублікував в журналі 'Стренд' роман 'Загублений світ', назва якого згодом стала звичною формулою, а сюжет - прообразом безлічі авантюрно-фантастичних оповідань про мандри в невідомі краї, відірвані від сучасної цивілізації і багаті доісторичною флорою і фауною.", "Артур Конан Дойль", 320, 1981, "/images/ae781818-0d37-4020-b716-7145611eb6ac.jpg", 0,  0),
    ("Skräcken i Vindelsjön", "Книга шведською. Або Нідерландською. Взяв почитати і зрозумів? Розкажи нам!", "Tony Manieri", 180, 2015, "/images/a3865772-0fc0-44cb-b21f-38a3a77964be.jpg", 0,  0),
    ("Остров сокровищ. Приключения Бена Ганна", "Книга Р.Делдерфілда не є продовженням Острова скарбів, швидше за все це доповнення, до того ж таке, що заслужило б схвалення самого Стівенсона. Що сталося з трьома піратами, залишеними на острові? Чи вдалося кому-небудь знайти зброю і срібні злитки? Як вийшло, що Бен Ганн знайшов скарб і, найголовніше, як міг стати піратом така нешкідлива людина, як Бен Ганн?", "Рональд Делдерфілда", 224, 1993, "/images/5b00c80f-ffd4-4517-9f2f-15640ff16b48.jpg", 0,  0),
    ("Дом с мезонином", "Розповідь Антона Павловича Чехова, написана в 1896 році. У творі відбилися життєві враження і особистий досвід автора, відтворена атмосфера місць, які відвідував письменник. Образи головних героїв співвідносяться з реальними людьми з числа знайомих Чехова.", "А. П. Чехов", 302, 1983, "/images/01329c88-ad91-4b9d-8a6a-948912223c37.jpg", 0,  0),
    ("Создание web-страниц и web-сайтов. Самоучитель", "Ця книга навчить вас реалізувати заповітну мрію Web-мандрівника: самовиражатися на декількох яскравих Web-сторінках власного сайту. Ви дізнаєтеся, як влаштовані Web-сторінки, які програми застосовуються для їхнього створення і як з ними працювати.Відмітна особливість книги — використання методики справжнього самовчителя. З найперших сторінок книги, керуючись покроковими інструкціями, ви навчитеся розміщувати на Web-сторінках текст і графіку, створювати власні малюнки та анімацію, оптимальні для Web, компонувати Web-сайт за допомогою супер-популярної програми FrontPage, а також вивантажувати свій сайт на безкоштовні сервери Web, і ваше творіння легко знайдуть всі інші мешканці Web.Ви станете справжнім Web-Дизайнером, здатним втілити всі свої творчі здібності у вигляді блискучого Web-сайту!", "В.М. Печніков", 464, 2005, "/images/f5222350-a2e6-48e3-9610-c2df09c3a673.jpg", 0,  0),
    ("Rework. Бизнес без предрассудков", "Про те, як почати свій бізнес. Якщо хочете — паралельно з основною роботою. І про те, як удосконалити ваш наявний бізнес, а точніше — ваші погляди на нього. З тим, щоб знайти небачену раніше ступінь свободи. Про те, який оптимальний розмір компанії, навіщо їй зростати, що і як потрібно насправді планувати і чи потрібно вчитися на помилках. І багато іншого, знайомого і несподіваного.Книга написана практиками. Джейсон Фрайд і Девід Хайнемайер Хенссон — вельми успішні інтернет-підприємці, що заснували легендарну 37signals. Компанію з 14 співробітників, продуктами якої користуються понад три мільйони людей по всьому світу! Читається на одному подиху. Розсіює всі сумніви на далеких підступах. Надихає. Збиває з пантелику. Виводить з зони комфорту. І дає корисну практичну базу. Ви і не підозрювали, що бізнес може бути ТАКИМ.Поскребіть по поверхні, і ви з'ясуєте, що мешканці реального світу наповнені песимізмом і відчаєм. Гірше за те, вони хочуть і інших затягнути у свою могилу. Якщо ви сповнені надій і амбіцій, вони постараються переконати вас, що реалізувати ваші ідеї неможливо. Вони скажуть, що ви даремно витрачаєте свій час. Не вірте їм. Такий світ може бути реальним для них, але це не означає, що ви маєте жити в ньому.", "Джейсон Фрайд и Девід Хайнемайер Хенссон", 192, 2017, "/images/8b99e9b6-0c52-418f-9a0f-579072d274ac.jpg", 0,  0),
    ("Реактивное программирование с применением RxJava", "У наші дні, коли програми асинхронні, а швидка реакція найважливіша властивість, реактивне програмування допоможе писати більш надійний, краще масштабується і швидше працює код.", "Томаш Нуркевіч і Бен Крістенсен", 358, 2017, "/images/accf0abe-0c73-4040-8f47-d022ae553314.jpg", 0,  0),
    ("Людина і зброя. Прапороносці.", "Лірико-філософський роман-реквієм, трилогія українського радянського письменника Олеся Гончара про події німецько-радянської війни. Роман написаний в 1946-1948 роках і складається з частин: Альпи (1946), Голубий Дунай (1947), Злата Прага (1948). Вперше опублікований в журналі Вітчизна, згодом — окремим виданням.", "Олесь Гончар", 717, 1984, "/images/57864503-b821-43dd-9979-8b80f71328cb.jpg", 0,  0),
    ("Проекти з використанням контролера Arduino", "У даній книзі розглянуті основні плати Arduino і плати розширення (шилди), що додають функціональність основній платі. Детально описано мову і середовище програмування Arduino IDE. Ретельно розібрані проекти з використанням контролерів сімейства Arduino. Це проекти в галузі робототехніки, створення погодних метеостанцій, 'розумного будинку', вендинга, телебачення, Інтернету, бездротового зв'язку (bluetooth, радіоуправління).", "Віктор Петін", 464, 2016, "/images/06effc3c-f8ef-4bca-a31b-c259d05a6a1b.jpg", 0,  0),
    ("Дитяча ілюстрована енциклопедія", "Книга для дітей, у яких є жага до знань. Енциклопедія призначена для дітей 8-12 і покликана розбудити і розвинути інтерес до історії та культури людства. Розділи книги розповідають про найбільші чудеса світу, створені людиною. Розрахована на широке коло читачів. Може бути використана як джерело додаткових знань при вивченні шкільного курсу історії.", "Клімов Андрій Анатолійович", 128, 2004, "/images/f7bdc3e5-04d0-4e0d-b114-abb809fa06e6.jpg", 0,  0),
    ("C# и платформа .NET", "Основна мета цієї книги - дати читачеві міцні знання синтаксису і семантики C #, а також розібрати особливості архітектури .NET. Після її прочитання ви познайомитеся з усіма основними областями, охопленими бібліотекою базових класів C #.", "Ендрю Троєлсен", 796, 2006, "/images/cf5b3242-1f2f-4e8f-b3bc-e57270bfc2dd.jpg", 0,  0),
    ("Приключения Тома Сойера. Приключения Гекльберри Финна", "Книги про пригоди Тома Сойєра і Гекльберрі Фінна, повні дивовижних подій і блискучого гумору, складають золотий фонд світової класики і залишаються в числі найбільш популярних в світі.", "Марк Твен", 383, 1985, "/images/c561e801-8d91-4886-937b-369ced20e9e2.jpg", 0,  0),
    ("Пиши, сокращай", "Максим Ільяхов і Людмила Саричева на конкретних прикладах демонструють, що добре, а що ні в інформаційних, рекламних, журналістських і публіцистичних текстах. Як писати листи, на які відповідатимуть, і розсилки, від яких не відписуватимуться. Як створювати дієві й коректні рекламні оголошення. Як викладати думки стисло, чітко та переконливо, без мовного сміття, фальші й штампів.", "Максим Ільяхов і Людмила Саричева", 440, 2019, "/images/132b3e69-889c-4d02-b85b-4e02a4af13c1.jpg", 0,  0),
    ("Новый деловой английский", "Підручник Нова ділова англійська (New English for Business) являє собою посібник, розрахований на 1200 аудиторських годин по створенню стабільних знань і навичок, які дозволили б бізнесменам адекватно користуватися англійською мовою для роботи з іноземними партнерами як в Росії, так і за кордоном.", "Дарска В.Г.", 672, 2005, "/images/15f04117-4446-4817-9825-1ef2cab5ceda.jpg", 0,  0),
    ("Phoenix in the ashes", "Бачення — Бачення любові та конфліктів, туги та Завтрашнього дня. З ізгоїв, які повинні подолати одержимість двох світів після Голокосту; і космонавт, який вважає, що її життєві пошуки зраджені пристроєм із чужого минулого. Принц, який став жебраком, з силою дракона; і феодальний кріпак, який зустрів володаря високих технологій, з магією торговлі. Кішка, яка втратила силу розуму, і Ткач мрій, який втратив розум. Жриця-варвар, чия краса кинула виклик світові та чия дитина могла розтопити серце чужого Бога — на скаліченій Землі, яка плавала під величезним циклопським оком.", "Джоан Віндж", 280, 1985, "/images/12ced842-5eb8-42b0-8ae8-d3adab70e1a8.jpg", 0,  0),
    ("Прокрастинація", "Відкладаєте важливі й дрібні справи на завтра, на потім, на останній тиждень перед дедлайном? Тоді ви — справжнісінький прокрастинатор. Відомі психологи Джейн Б. Бурка та Ленора М. Юен пропонують проникливий, чутливий, а іноді смішний погляд на проблему, яка зачіпає всіх: студентів та вчених, офіс-менеджерів та керівників, домогосподарок та продавців. Бо, напевне, навмисне затягування часу має свої причини, і їхнє коріння у нашому дитинстві та дорослому досвіді. Ця книжка — ефективний порадник із рекомендаціями, які допоможуть вам взятися до справи, оминаючи перешкоди прокрастинації, аби врешті успішно долати особистий шлях до психологічного зростання та успіху.", "Джейн Б. Бурка, Ленора М. Юен", 400, 2019, "/images/c0caed59-a468-4c56-adeb-f57616c86e38.jpg", 0,  0),
    ("У пошуках міста Богів. Траг послання стародавніх", "Автор - всесвітньо відомий офтальмолог і мандрівник - ділиться в цій книзі своїм відкриттям - законом чотирьох шісток, якому підпорядковується наша планета.", "Ернст Мулдашев", 536, 2002, "/images/78cb8340-d4f2-4be4-b702-02e2489bce17.jpg", 0,  0),
    ("Разработка встроенных систем с помощью микроконтроллеров PIC", "Завдяки корисним прикладів і ілюстрацій, ця книга дає глибокі пізнання в сфері проектування систем за допомогою мікроконтролерів PIC, а також - програмування цих пристроїв на асемблері і С.", "Тім Уілмсхерст", 544, 2008, "/images/ea0aeecd-887d-4d5f-9f65-dd4d870813e2.jpg", 0,  0),
    ("Аналіз програмного коду на прикладі проектів Open Source", "Книга присвячена важливому аспекту програмування, недостатньо освітленому в літературі - читання та аналізу програмного коду на мовах високого рівня з метою доопрацювання, вилучення готових технічних рішень або вивчення нових методів. Даються цінні рекомендації щодо поліпшення стилю програмування. Виклад проілюстровано великою кількістю прикладів, взятих з великих програмних проектів з відкритим кодом, що знаходяться на компакт-диску. Книга призначена для підвищення кваліфікації програмістів. Може бути корисна студентам і викладачам відповідних спеціальностей, а також початківцям програмістам.", "Діомідіс Спінелліс", 528, 2004, "/images/fa2d0690-be3b-4c5a-9a7c-82df0d4f9811.jpg", 0,  0),
    ("Python для дітей. Веселий вступ до програмування", "Програмуйте із задоволенням! Ця книжка легко та невимушено допоможе вивчити основи однієї з найпопулярніших сучасних мов програмування – Python, що завдяки простому і зрозумілому синтаксису особливо пасує для новачків.", "Джейсон Р. Бріггс", 400, 2017, "/images/6e5f7334-00f2-464e-b6d2-010ac694c5c8.jpg", 0,  0),
    ("Волшебник изумрудного города", "Казкова повість Олександра Мелентьевич Волкова, написана в 1939 році на основі казки американського письменника Лаймена Френка Баума Чарівник країни Оз з деякими змінами. У 1959 році вийшло нове видання книги, значно перероблене автором. У цьому виданні вперше з'явилися ілюстрації художника Л. В. Володимирського. Згодом книга була перероблена ще раз.", "Олександр Волков", 130, 1991, "/images/18e4e0ec-055f-4c0a-9296-05d4c0f5ce72.jpg", 0,  0),
    ("Тысяча и одна ночь", "Тисяча і одна ніч - пам'ятник середньовічної арабської і перської літератури, збори оповідань, обрамлені історією про перського царя Шахріяре і його дружину на ім'я Шагразада.", "Арабскі сказки", 352, 1977, "/images/b9511082-05bb-45a2-ae87-f0be23e20725.jpg", 0,  0),
    ("Prioritizing Web Usability", "After more than a decade of Web usability research, we literally have thousands of guidelines for making better websites. But what are the most important ones that all designers need to know? That's what Prioritizing Web Usability is about. A second goal of the book is to update the early Web usability guidelines we published in the 1990s. All the guidelines found since 2000 continue to hold, but what about findings from the studies we conducted 1994 to 1999? The book compares these old studies with more recent ones and explains which of the old guidelines should still be followed. 406 pages, heavily illustrated, in full color.", "Jakob Nielsen, Hoa Loranger", 406, 2006, "/images/c4a5cc05-8e9d-4f55-8dc0-e849b0f33c2a.jpg", 0,  0),
    ("Искусство торговли", "Відомо, що більшість покупців - жінки. Але слова клієнт, покупець - чоловічого роду. Щоб не захаращувати текст закінченнями клієнт (ка), по-купатель (ка), ми обходимося без них, маючи на увазі, що покупці і клієнти - це і чоловіки, і жінки. Автор даної книги, яку читач тримає зараз в руках, призводить тільки ті рекомендації, які перевірені і добре працюють в наших умовах. І судить про це не з чуток, а виходячи з досвіду численних занять, проведених ним з персоналом торгових фірм - від рядових продавців і торгових агентів до організаторів, менеджерів торгового бізнесу. У мистецтві продажу ми далеко відстали від країн з усталеною ринковою економікою. Доводиться тепер наздоганяти, вчитися на ходу. Але ось проблема: вчитися по перекладним книгам або книгам, написаним нашими авторами, але буквально переказує західний досвід, далеко не в усьому на користь. Чому? Вся справа в нашій психології, в менталітеті нашого людини. Він в корені відрізняється від західного споживача. Ось типовий приклад з одного такого видання. Автор рекомендує торговому агенту починати бесіду з потенційним клієнтом при першій зустрічі з питання Як справи? або Як йдуть справи сьогодні?. Питання такого сорту ввічливі, ненав'язливі і найчастіше втягують покупця в розмову. Цим ви демонструєте, що зосереджені на покупця. Така манера поведінки у нас вважається розв'язною, якщо не нахабною.", "В. П. Шейнов", 414, 2003, "/images/3725c92e-4a7b-48e1-b83c-13ba0be918f2.jpg", 0,  0),
    ("Мудакам тут не місце", "Книга Роберта Саттона - викладача Стендфордського університету - одразу після публікації увійшла в списки бестселерів за версією видань New York Times, Wall Street Journal, USA Today та Business Week. Хто такі мудаки і що вони роблять на вашій роботі? Як виявити мудака і поставити його на місце? Саттон наводить результати досліджень, які свідчать про те, що мудаки на робочу місці не просто принижують колег, негативно впливають на їхню самооцінку, а ще й приносять компанії фінансові збитки. Як запровадити правило Мудакам тут не місце у вашій компанії і як самому не перетворитися на покидька - просто і лаконічно розповість автор.", "Роберт Саттон", 176, 2016, "/images/1f9b7c0d-aa27-4f4a-86bd-fd68db9d9027.jpg", 0,  0),
    ("Алые паруса", "Якщо ви любите добрі казки з романтичним сюжетом і щасливим фіналом, радимо прочитати книгу Пурпурові вітрила. Це казка для будь-якого віку і на всі часи. Вона дарує надію на краще і змушує повірити в чудо без фей і чаклунства. Виявляється, щоб мрія стала реальністю, чарівники не потрібні.", "Олександр Грін", 366, 1989, "/images/cb046245-00de-42b0-92b9-c8179c6547c6.jpg", 0,  0),
    ("Старик и море", "Ніхто не заздрив життю старого кубинського рибака Сантьяго. У рідному селі він уславився диваком і люди його цуралися. Але не Мандоліно - сусідський хлопчисько, для якого Сантьяго став справжнім другом. Одного разу старий рибалка відправився в море і йому посміхнулася удача - величезна риба попалася на гачок. Тепер йому доведеться випробувати всю свою волю щоб утримати її...", "Ернест Хемінгуей", 94, 1981, "/images/1f4e67b4-6259-46ca-b915-29f8316a57de.jpg", 0,  0),
    ("Его прощальный поклон", "Роки йдуть, і Шерлок Холмс, звичайно, не молодіє. Він поступово відходить від справ, і Ватсон тепер більше згадує про їх колишніх розслідуваннях, що розповідає про нові. Втім, часом і поточні події змушують героїв згадати молодість...", "Артур Конан Дойл", 286, 2016, "/images/e751801a-15c3-450c-90e2-d689808774f4.jpg", 0,  0),
    ("Мобильные роботы на базе Arduino", "Керівництво для початківців конструкторів написано у формі практичних проектів з побудови мобільних роботів. Для їх реалізації обрана популярна платформа Arduino і єдина базова чотириколісна конструкція. Складність проектів зростає від простої, керованої з пульта 'машинки' до інтелектуального робота. Розказано, як управляти моторами, здійснювати збірку механіки і електроніки, програмувати основні функції і управляти роботом. Роботи зможуть обходити перешкоди, вибиратися з заплутаних лабіринтів, шукати кеглі і визначати їх колір, орієнтуватися по електронному компасу і навіть балансувати на двох колесах. У 2-му виданні оновлені всі алгоритми, додані проекти гіроскоп-акселерометр, робот, що говорить, голосове управління роботом, механічна 'рука' і інше.", "Михайло Момот", 288, 2017, "/images/f3abc3e2-21e0-4524-bbba-5b86f5723ab3.jpg", 0,  0),
    ("Измерение, управление и регулирование с помощью AVR микроконтроллеров", "Завдяки своїй високій продуктивності при порівняно невисокій ціні, серія AVR від компанії Atmel займає провідне місце на ринку мікроконтролерів. Саме використання цієї серії в методиках вимірювання, управління і регулювання і присвячена дана книга, яка містить безліч прикладів схем, розроблених і протестованих самим автором.", "Вольфганг Трамперт", 208, 2007, "/images/977e5a5d-9652-4969-905e-b9c3367f1987.jpg", 0,  0),
    ("Великие архитектурные проекты", "Будь-яке місто має неповторний вигляд, який проявляється в незвичайному дизайні будівель або організації районів. Характер міста багато в чому залежить саме від архітектури, яку недарма називають застиглою музикою. Архітектура виникла в стародавні часи, так як будівництво відноситься до найбільш стародавніх видів людської діяльності. Історія будівництва бере свій початок ще з природних печер, де проживали первісні люди. У книзі представлені великі архітектурні проекти, що створювалися протягом всієї історії людства.", "Адамчік Мирослав В'ячеславович", 95, 2008, "/images/5b7c1110-dc0e-4a61-988d-cb8dd218bbf3.jpg", 0,  0),
    ("Android разработка приложений для чайников", "У наш час мільярди користувачів носять в кишені портативні пристрої, і в половині з них встановлена ​​операційна система Android. Навіть якщо ви ніколи не розробляли програми для мобільних пристроїв, прочитавши цю книгу, ви зможете втілити свої неймовірні ідеї в сучасні програми для платформи Android.", "Донн Фелкер", 336, 2012, "/images/393846cb-c7cf-425e-a242-8b7f930335ab.jpg", 0,  0),
    ("Де не де Зелене місто над Дніпром", "Мальовані історії та роздуми.", "Маша Катрич, Патріс Петерсон", 40, 2019, "/images/e7c326d5-86e6-4b73-a394-00623ead80f8.jpg", 0,  0),
    ("Компьютерная графика и стандарт OpenGL", "Припускаючи, що читачі абсолютно не знайомі з комп'ютерною графікою, автори виклали основні принципи, необхідні для розробки, використання і розуміння систем комп'ютерної графіки. У даній книзі математика і фізика органічно поєднуються з програмуванням з використанням OpenGL. Пропонований підхід оптимальний, якщо ставиться завдання вивчити теоретичну і практичну сторони дво- і тривимірної графіки.", "Херн Бейкер", 1168, 2005, "/images/3ac51790-7200-4498-9663-d4cfd456c8aa.jpg", 0,  0),
    ("Оголошено вбивство", "Хто вбив… убивцю? Оголошення в щоденній газеті неабияк заінтригувало мешканців англійської провінції: їх запрошено на дивну виставу — вбивство. Охочі подивитися на це, а їх виявилося чимало, певно, забули, що весь світ — театр, а люди в ньому — актори, а не глядачі. Тож ролі жертв уже розподілено… Тільки старенькій міс Марпл до снаги викрити загадкового постановника кривавої драми.", "Агата Крісті", 298, 2017, "/images/e58a716b-41e8-4e18-a611-1933e3ae2dce.jpg", 0,  0),
    ("Большая книга CSS", "Технологія CSS3 дозволяє створювати професійно оформлені сайти, але тонкощі цієї мови можуть виявитися досить складними навіть для досвідчених веб-розробників. Повністю перероблене видання цієї книги допоможе вам підняти навички роботи з HTML і CSS на новий рівень; воно містить безліч цінних порад, описів прийомів, а також інструкції, написані в стилі довідкового керівництва. Веб-дизайнери, як початківці, так і досвідчені, за допомогою цієї книги швидко навчаться створювати красиві веб-сторінки, які блискавично завантажуються як на ПК, так і на мобільні пристрої.", "Девід Макфарланд", 720, 2010, "/images/60e5a395-8525-4cac-b373-a00e8eb8af26.jpg", 0,  0),
    ("Assembler 2-е издание. Учебник для вузов", "У підручнику розглядаються питання програмування мовою асемблера для комп'ютерів на базі мікропроцесорів фірми Intel. Основу книги становить матеріал, який є частиною курсу, читаного автором у вищому навчальному закладі і присвяченого питанням системного програмування. У порівнянні з першим виданням підручник істотно перероблений. Виправлені помилки і неточності. Додано опис команд для Intel-сумісних процесорів (до Pentium IV включно). Книга буде корисна студентам вузів, програмістам і всім бажаючим вивчити мову Assembler.", "В.І. Юров", 637, 2004, "/images/e823a405-4596-4173-8f4f-b45221a397a2.jpg", 0,  0),
    ("Записки о Шерлоке Холмсе", "Спогади Шерлока Холмса - збірка оповідань про знаменитого детектив і його друга доктора Ватсона, який з ініціативи Джорджа Ньюнса — видавця щомісячного ілюстрованого британського журналу Стренд мегезін — в 1894 році був випущений окремою книгою. Як і перші розповіді про Холмса, історії Спогадів проілюстрував художник Сідні Пейджет.", "Артур Конан Дойль", 288, 2016, "/images/a7d2c829-87c7-4e24-a0dd-c47035fdbe41.jpg", 0,  0),
    ("Rework. Ця книжка змінить ваш погляд на бізнес", "Настав час відкинути традиційні уявлення про ведення бізнесу! Довідайтеся, як започаткувати справу чи вдосконалити власний бізнес. Автори крок за кроком навчать вас, як почати справу, пояснять, чому вам треба менше, ніж ви думаєте, порадять, як зробити так, щоб про вас дізналися, порекомендують, кого і коли варто взяти на роботу, а також як з усім цим упоратися. Книжка надихає та дає практичну базу для руху вперед! Ця книжка для: підприємців, які відчувають себе природженими створювати, очолювати й перемагати власників малого бізнесу, які шукають виходу за усталені рамки тих, хто поки лише мріє про власну справу", "Джейсон Фрайд та Девід Хайнемайєр Хенссон", 172, 2017, "/images/e22aadeb-2afa-4089-b892-916ed186ed33.jpg", 0,  0),
    ("Бизнес для программистов. Как начать своё дело.", "Книга являє собою добірку кращих есе автора — успішного IT-менеджера, який завоював авторитет і визнання в софтверном бізнесі. Тематика видання зачіпає весь бізнес-процес виробництва програмного забезпечення: від ідеї нового проекту і підбору команди до маркетингу та розповсюдження готового продукту. Без сумніву, думки, ідеї та рекомендації Еріка Сінка будуть цікаві як розробникам ПЗ, так і менеджерам проектів. Ця книга просто необхідна всім, хто хоче заробляти гроші на виробництві софта.", "Ерік Сінк", 251, 2008, "/images/53c9c34d-057d-4ff8-a958-3c23b564531e.jpg", 0,  0),
    ("Найкоротша історія часу", "Твір написаний живою мовою і розрахований на широке коло читачів без академічних знань з фізики. Це скорочене й доповнене новими відкриттями в науці перевидання бестселера Стівена Гокінґа Коротка історія часу, який розійшовся по світу накладом понад 10 млн примірників! Ви дізнаєтесь про появу Всесвіту, чорні діри, теорію суперструн і квантову механіку.", "Стівен Гокінг і Леонард Млодінов", 159, 2018, "/images/3d3099f0-4bb6-4818-bc6f-98f9d6e8f53d.jpg", 0,  0),
    ("Львів: перечитування міста", "Ця книжка — спроба відшукати образ Львова в літературі XX і XXI ст., що творилася різними мовами. Бо саме в цей час, на думку авторки, найкраще видно способи конструювання різних національних наративів про Львів, їхні взаємні зв’язки та конфлікти. Це спроба критично розглянути популярний міф Leopolis multiplex, аби допомогти краще зрозуміти львівського іншого, незалежно від того ким він є — українцем, поляком, росіянином чи євреєм.", "Катажина Котинська", 240, 2017, "/images/fbb8c3a5-bca4-46d7-92fb-f0df2aa04476.jpg", 0,  0),
    ("Любов та бюджет", "Якщо ви мешкаєте в Україні й вирішили написати книжку, яка порушує найзамовчуваніші, найнезручніші теми, то це не буде книжка про секс. Це буде книжка про гроші. Звісно, не про абстрактні гроші заокеанських мільярдерів або вітчизняних олігархів, а про свої, рідні.", "Любомир Остапів", 216, 2017, "/images/79e634ca-aa93-4103-9913-1608e48fb57d.jpg", 0,  0),
    ("(Не)задоволення публічними просторами", "Збірку статей (Не)задоволення публічними просторами присвячено публічним просторам, як найхарактернішим, і разом з тим найбільш проблемним в наш час вимірам і місцям урбаністичного способу життя.", "С.Шліпенко, І.Тищенко", 342, 2017, "/images/cc24a0d5-d62c-4395-a0df-a1dae491d46e.jpg", 0,  0),
    ("Сон номер 9", "Разом з юним Ейдзі Міяке читач поринає у вир токійського життя, переживає його фантазії і сни, гортає листи його матері-алкоголічки і щоденники людини-торпеди, зустрічається з безжалісними якудза, Джоном Ленноном і богом грому.", "Девід Мітчел", 416, 2007, "/images/6dbcd990-3160-4fd3-81d9-8519e3ab394c.jpg", 0,  0),
    ("Бізнес-курс англійської мови", "Книга охоплює широкий діапазон мовних штампів, текстів, вправ та діалогів. Мета посібника — навчити основам ділового спілкування в усних і письмових формах. Навчальний курс складається з 12 уроків, кожен з яких включає в себе основний текст-полілог, словник-мінімум, загальноосвітні та граматичні коментарі, тести і текст для читання з всебічною інформацією про англомовні країни. В основі побудови книги — принципи інтенсивного навчання. Для широкого кола осіб, які вивчають англійську мову в групах або самостійно і прагнуть в максимально короткі терміни опанувати основи ділового спілкування в типових ситуаціях.", "І.С. Богацкий, Н.М. Дюканова", 352, 2010, "/images/b73614cf-f1b9-45b0-9f22-2af87a28f491.jpg", 0,  0),
    ("'Захисники галактики' Влада і криза в чоловічому світі", "Якими чоловіки бачать себе і якими їх бачать інші, чого від них сподіваються і вимагають, за що засуджують і карають, як це позначається на можливостях самореалізації в суспільстві та загальній якості життя, як історично розвивалися уявлення про маскулінність і чому нині можемо говорити про кризу маскулінності — Тамара Марценюк пропонує відповіді на ці та багато інших запитань, спираючись на широку джерельну основу та власну дослідницьку роботу.", "Тамара Марценюк", 256, 2020, "/images/57962fad-0ddf-491a-9351-da72ace832c6.jpg", 0,  0),
    ("Психологія 101", "Коротко про найголовніше, зрозуміло про надскладне, захопливо про професійне! Книга-бестселер американського журналіста, блогера та письменника Пола Клейнмана подарує неймовірне задоволення й познайомить у науково-популярній формі з основами психологічних концепцій та експериментів і з маловідомими фактами з життя видатних учених-психологів.", "Пол Клейнман", 240, 2016, "/images/9115c248-ef01-4e56-bae0-41a0f30a8fc6.jpg", 0,  0),
    ("Дивовижні пригоди всередині тіла. Велика подорож від голови до п'ят", "Захоплива мандрівка! Суцільні несподіванки! Від головного мозку і до стоп! Ця подорож переверне ваше уявлення про власне тіло, допоможе позбутися багатьох упереджень і отримати безліч цікавих і корисних знань про невидимі процеси, які в ньому відбуваються щодня.", "Гевін Френсіс", 256, 2016, "/images/863df2ee-fc27-4d24-8426-34313535b861.jpg", 0,  0),
    ("Ассемблер. Разработка и оптимизаци Windows-приложений", "У книзі розглядається один з ефективних методів оптимізації програм — використання мови асемблера, описана методика розробки окремих модулів на ньому для застосування в програмах на мовах високого рівня, показано, як за допомогою асемблера можна розробити повнофункціональні Windows-програми.", "Юрій Магда", 544, 2003, "/images/1883341a-cf08-4d62-ad51-1e6875ce25c6.jpg", 0,  0),
    ("Red Hat Linux", "Маючи приблизно 20 мільйонів користувачів у всьому світі, Linux — це найбільш обговорювана історія успіху з відкритим кодом та вільним програмним забезпеченням. Навіть прихильників Microsoft Windows та Macintosh від Apple приваблює неперевершена стабільність та гнучкість Linux, але вивчення операційної системи в стилі Unix може бути непростим завданням. Друге видання “Вивчаючи Red Hat Linux” дає новачкам саме те, що їм потрібно для початку.", "Білл Маккарті", 378, 1999, "/images/45d2bed0-acaf-4f10-a5f5-cbd90d594daf.jpg", 0,  0),
    ("PGP: Pretty Good Privacy", "Використання Інтернету розширюється поза очікуванням. Коли корпорації, урядові установи та пересічні громадяни починають покладатися на інформаційну магістраль для ведення бізнесу, вони усвідомлюють, наскільки важливо захищати свої комунікації — і тримати їх у таємниці від сторонніх очей, і гарантувати, що їх не змінюють під час передачі. Шифрування, яке донедавна було езотеричним полем, що цікавило лише шпигунів, військових та кількох науковців, забезпечує механізм для цього. PGP, що розшифровується як Pretty Good Privacy, - це безкоштовна та широко доступна програма шифрування, яка дозволяє вам захистити файли та електронну пошту. Написаний Філом Циммерманом і випущений у 1991 році, PGP працює практично на всіх платформах і став дуже популярним як у США, так і за кордоном. Оскільки він використовує найсучаснішу криптографію з відкритим ключем, PGP може використовуватися для автентифікації повідомлень, а також для збереження їх у таємниці.", "Сімсон Гарфінкель", 393, 1995, "/images/1291b27f-f1a3-47de-847f-2d733083fcd5.jpg", 0,  0),
    ("Perl: наглядный курс программирования", "Виклад матеріалу в цій книзі побудовано на розгляді досить простих прикладів, за допомогою яких можна швидко навчитися створювати потужні сценарії і програми. Для того щоб отримати максимальну користь з книги, її необхідно читати послідовно, починаючи з першого розділу і закінчуючи останнім. У кожній з наступних глав розглядається новий матеріал і способи вирішення певних завдань, які багато в чому базуються на розумінні того, що було пройдено в попередніх розділах.", "Пол Уайтхед", 280, 2001, "/images/8a20bb8e-469a-4bab-a826-d84149857faa.jpg", 0,  0),
    ("Visualizing Data", "Використовуючи завантажуване середовище програмування, розроблене автором, Візуалізації даних демонструє методи точного представлення даних в Інтернеті та в інших місцях, разом із взаємодією користувачів, анімацією тощо.", "Бен Фрай", 366, 2008, "/images/1e634c63-4adf-4b91-89c2-67a3fee85d2c.jpg", 0,  0),
    ("Node.js Разработка серверных веб-приложений на JavaScript", "Книга 'Node.js Розробка серверних веб-додатків на JavaScript' присвячена розробці веб-додатків в Node.js — платформі, яка виводить мову JavaScript за межі браузера. Це дозволяє використовувати його в серверних додатках. Швидкий движок JavaScript, запозичений з браузера Chrome, лежить в основі платформи. Крім того, додано швидку і надійну бібліотеку асинхронного мережного введення/виведення. Девід Херрон описує установку та експлуатацію Node.js, каркаси розробки додатків Connect і Express. Розповідає про роботу з базами даних.", "Девід Херрон", 144, 2012, "/images/5489a5b2-783f-4509-a6a7-0d4dded31f9e.jpg", 0,  0),
    ("Summa", "SUMMA – книжкова версія спільного (у задумі – мультимедійного) проекту письменника Юрія Іздрика та журналістки Євгенії Нестерович. Спроба долучитися до синкретично-синтетичної теорії всього мала на меті, серед іншого, створення своєрідного довідника для тих, хто випадково потрапив на цю планету. SUMMA і є цим довідником, а також конспектом усього проекту.", "Іздрик Юрій, Нестерович Євгенія", 144, 2016, "/images/ab336577-7dc5-4cce-8b6b-6dcf5a256d15.jpg", 0,  0),
    ("Язык программирования С", "Перед вами класична книга з мови програмування C, написана самими розробниками цієї мови і витримала в США вже 34 перевидання! Книга є як практично вичерпним довідником, так і навчальним посібником з найпоширенішої мови програмування C. Друге видання книги було істотно перероблено в порівнянні з першим в зв'язку з появою стандарту ANSI C, для якого вона частково послужила основою.", "Брайан Керніган, Денніс Ритчи", 304, 2015, "/images/6fddf8d1-3729-41ce-82ad-7f775f6b5976.jpg", 0,  0),
    ("Ворошиловград", "Це мала бути коротка подорож - провідати брата, який тримав бензоколонку в донбаських степах. Та, опинившись у місті свого дитинства, Герман мало не назавжди влипає в тутешній пейзаж. Брат зникає, на його бізнес зазіхають місцеві мафіозі, старі друзі мають дивні справи… Гєра має визначитися із хиткою реальністю, минулим, що владно ввірвалось до його життя, та майбутнім, щодо якого є певні сумніви… У цьому романі є все: цигани, рейдери, степ, занедбані будівлі, нескінченні дороги, футбольні баталії, друзі юнацтва, жінки, секс, алкоголь, спогади про мрії дитинства та, звичайно, джаз. Бо, як каже герой, потрібно слухати музику, яку любиш. Тільки не давати чужим свої навушники.", "Сергій Жадан", 320, 2018, "/images/e1e4310b-e723-407f-959a-a125fb4c1e1e.jpg", 0,  0),
    ("1000 та 1 поворот 'Бум'. Том 2", "Авторка книги, голова Національної програми з безпеки дорожнього руху Traffic Challenge Ольга Дробишева впевнена, що за допомогою цієї книги діти краще сприйматимуть правила дорожнього руху, стануть обачнішими, а батькам буде легше розповісти своїм дітям про небезпеки, які чатують на пішоходів і водіїв та пасажирів, адже діти є найбільш незахищеними учасниками дорожнього руху.", "Оля Дробишева", 24, 2017, "/images/3a0e5b86-ab6a-4874-b3e2-2bae691fced8.jpg", 0,  0),
    ("Введение в ZBrush 4", "З цією книгою ви навчитеся моделюванню дивовижних по своїй реалістичності істот, людей і неживих предметів в одній з кращих програм 'цифрового ліплення' Zbrush.", "Ерік Келлер", 768, 2012, "/images/179593ba-e935-439d-85c2-d0c0998ef2ce.jpg", 0,  0),
    ("Дмитро Дубілет. Бізнес на здоровому глузді. 50 ідей, як домогтися свого", "Дмитро Дубілет – один із найбільш медійних, позитивних та ефективних підприємців країни. Здається, що все, за що він береться, йому вдається – і бізнес-проєкти, і проєкти для держави. У 20 років він продав перший бізнес з оцінкою в $ 100 тис. У 28 став IT-директором ПриватБанку. Разом з партнерами заснував monobank, який привернув 1,6 млн клієнтів за два роки. Планував запустити мобільний банк Koto у Великобританії, але став міністром і пішов з бізнесу. У книзі Дмитро Дубілет. Бізнес на здоровому глузді буде 9 розділів, заснованих на особистому досвіді Дмитра, про практичні речі і про те, що надихає: про розробку бізнес-ідей, побудову команди, маркетинг, продажі, а також 50 ідей, як втілити принципи Дмитра і домогтися свого.", "Тімур Ворона", 256, 2020, "/images/30bc62ac-510d-4a3f-9d5b-2d0d6f4dbc10.jpg", 0,  0),
    ("Учебный курс Microsoft. Развертывание и настройка Windows Server 2008", "Ця книга — докладне керівництво з розгортання та підтримки операційної системи Windows Server 2008. У книзі дані покрокові інструкції, описаний механізм розгортання і процеси настройки нової ОС, її основних компонентів: серверної системи зберігання даних і кластерів, служб терміналів, веб-додатків, веб сервера, служб FTP, SMTP, Windows SharePoints Services і Windows Media 2008.", "Дж. К. Макін, Аніл Десаі", 600, 2011, "/images/be76db60-cdad-41c7-8c1d-8dc7815acb1d.jpg", 0,  0),
    ("The Four Steps to the Epiphany", "Класичний бестселер, що дав життя більш ніж 10 000 стартапам і корпоративним новинам, Чотири кроки до осяяння отримав звання одного з найвпливовіших і практичних бізнес-посібників всіх часів. Книга вказала на нові висоти в способах створення бізнесу з нуля. Вона стала першим голосом, пояснив, що стартап зовсім не є зменшеною проекцією великих вже існуючих компаній, навпаки, нові підприємства - це щось зовсім відмінне.", "Стів Бланк", 281, 2006, "/images/e32b7bef-4d72-424e-af7e-1130a329e923.jpg", 0,  0),
    ("Відродження", "1962 рік, Гарлоу. Джеймі було шість, коли в їхньому містечку з'явився новий пастор - молодий і розумний Чарльз Джейкобз. Та коли його родина загинула у страшній аварії, священик прокляв небо і зрікся Бога... Кілька років по тому Джеймі опинився на межі життя й смерті, і колишній пастор врятував його. Але чому, прийшовши до тями, хлопець відчув, ніби сталося щось жахливе? І чому тепер йому сняться мертві дружина й син Джейкобза?", "Стівен Кінг", 416, 2015, "/images/1a4edb46-9373-4eb5-92f5-dd26f4b16f36.jpg", 0,  0),
    ("Голуб Геннадій", "Знайомтеся, це Геннадій. Ні, він не проста безмозкий птах, а досить-таки принциповий голуб з характером. Його норми моралі підіймають планку високо над головами побратимів, сенс життя яких зводиться до курликанням і клювання хлібних крихт. Короткі історії з життя голуба Геннадія розповідають про його поглядах на життя, принципах і проблемах, з якими доводиться стикатися сучасному птицю. Спочатку Геннадій був персонажем веб-коміксу, і за задумом автора вже на четвертій сторінці повинен був трагічно загинути, адже автор відчуває до голубів глибоку неприязнь. Геннадій же у свою чергу ненавидить людей, так що у них це взаємно. І ось так парадокс: саме народна любов врятувала його від забуття і дала життя цій книзі.", "KORO", 64, 2019, "/images/7e7c1851-617b-4310-898f-38c061231f5d.jpg", 0,  0),
    ("Исповедь. О жизни.", "В дане видання увійшли два найважливіші релігійно-філософські праці Л. М. Толстого — 'Сповідь' і 'Про життя'. Свого часу ці твори були заборонені до публікації духовною цензурою. І якщо 'Сповідь' розійшлася по всій Росії в рукописних копіях, то твір 'Про життя' широкому читачеві не був відомим. У Сповіді Толстой відтворював прихований, зовні малопомітний процес, який протягом декількох десятиліть відбувався в його духовному житті. 'Зі мною трапився переворот, який давно готувався в мені і зачатки якого завжди були в мені', — писав він. Толстой з увагою вдивлявся в прожиті роки, намагаючись відповісти на питання, що хвилювали його, найважливішим з яких стало питання віри. 'Сповідь' — перший твір на цьому шляху, 'Про життя' — книга підведення підсумків.", "Лев Толстой", 286, 2017, "/images/ed1c0ac1-5c09-49e4-b020-b2859f5b24ac.jpg", 0,  0),
    ("Building Scalable Web Sites", "Вивчіть прийоми торгівлі, щоб ви могли швидко створювати та розробляти додатки, які масштабуються — без проблем та домовленостей про рівень послуг, пов’язаних із серверами корпоративних додатків та власними продуктами програмування та баз даних.Вибраний із досвіду провідного розробника Flickr.com, книга пропонує техніки для створення швидких вебсайт, якими користувачі зможуть користуватися. Створення популярних сайтів вимагає набагато більше, ніж швидке обладнання з великою кількістю пам'яті та місця на жорсткому диску. Потрібно подумати про те, як з часом зростати, як зробити однакові ресурси доступними для аудиторії з різними очікуваннями та як змусити команду розробників працювати на сайті, не створюючи нових проблем для відвідувачів та один для одного.Незалежно від того, починаєте ви невеликий вебсайт із надією на збільшення чи у вас вже є велика система, яка потребує обслуговування, ви знайдете книгу “Створюємо вебсайт, що масштабуються” обов'язковою для своєї бібліотеки.", "Кел Хендерсон", 330, 2006, "/images/747e4f53-6793-49ea-ad63-bb329cd05672.jpg", 0,  0),
    ("Розмови з донькою про економіку. Коротка історія капіталізму", "Як пояснити нерівність народів та держав і чим може зарадити демократія? Світом керують гроші чи їх відсутність? Як працює економіка в ринковому суспільстві? Куди веде людство розвиток технологій і чи є в людини шанс залишитися людиною і вберегти планету від самої себе? На ці та інші теми зі своєю 13-річною донькою Ксенею розмовляє відомий грецький економіст Яніс Варуфакіс. Пояснити просто й цікаво складні, але дуже важливі речі йому допомагають: цар Едіп та Ікар, Архімед, австралійсь­кі аборигени, доктор Франкенштейн, Фауст і навіть агент Сміт з культової Матриці.", "Яніс Варуфакіс", 168, 2019, "/images/e06c4d90-983a-443b-be7c-e71cbaa20f3b.jpg", 0,  0),
    ("Атлант розправив плечі. Несуперечність", "Є такі книжки, що випереджають свій час. З’явившись у книжковому магазині у 1957 році, цей твір довгий час критикувався літераторами, залишаючись на задвірках літературного процесу і читацької уваги. Але через кілька десятиліть трилогія стала викликати інтерес спочатку в США, а потім і в усьому світі. У 2000 році стався справжній бум популярності, роман перевидавався багатомільйонними тиражами, а соціальні мережі захлеснула хвиля захоплених рецензій і тисяч цитат. У 2009 році роман отримує ряд нагород і стає бестселером. Купити книгу Атлант розправив плечі варто любителям якісної антиутопії з сильним соціальним, економічним і філософським підґрунтям. Сьогодні відгуки про книги Атлант розправив плечі серед профільних видань і звичайних читачів виключно позитивні. На початку століття Бібліотека Конгресу США провела опитування для визначення найкращих книг. Ця американська трилогія стала другим після Біблії виданням, яке, на думку респондентів, здатне привести до зміни світогляду.", "Айн Ренд", 390, 2018, "/images/d9dd5289-5aa7-432e-abfa-2625f5faa4f6.jpg", 0,  0),
    ("CLR via C# программирование на платформе microsoft .NET framework 4.0 на языке С#", "Ця книга, що виходить в третьому виданні і вже стала класичним підручником з програмування, детально описує внутрішній устрій і функціонування загальномовного виконуючого середовища (CLR) Microsoft .NET Framework версії 4.0. Написана визнаним експертом в області програмування Джеффрі Ріхтером, який багато років є консультантом команди розробників .NET Framework компанії Microsoft, книга навчить вас створювати по-справжньому надійні додатки будь-якого виду, в тому числі з використанням Microsoft Silverlight, ASP.NET, Windows Presentation Foundation і т.д. Третє видання повністю оновлено відповідно до специфікації платформи .NET Framework 4.0 і принципам багатоядерного програмування.", "Джеффірі Ріхтер", 927, 2012, "/images/16555964-703f-4176-9223-4db86912d6ff.jpg", 0,  0),
    ("Як бути усвідомленим", "Чи хотіли б ви почуватися менш схильним до стресу, жити спокійніше і більш змістовно? Навчіться цінувати поточний момент і прийміть усе, що підносить вам життя. Запропоновані способи допоможуть вам краще зрозуміти себе й досягти цього й багато іншого. Прості поради, висловлювання, що надихають на самовдосконалення, і кумедні жарти наповнять кожен ваш день вдячністю і спокоєм.", "Анна Барнс", 160, 2017, "/images/d1b4f312-d8e2-4db7-92a2-8fed331ea8db.jpg", 0,  0),
    ("Qt 4.8 профессиональное программирование на С++", "Книга присвячена розробці додатків для Windows, Linux і Mac OS X з використанням бібліотеки Qt версії 4.8. Докладно розглянуто можливості, що надаються цією бібліотекою, і описано особливості, що вигідно відрізняють її від інших бібліотек. Описано інтегроване середовище розробки Qt Creator. Показано створення призначених для користувача інтерфейсів за допомогою Qt Quick і QML. Книга містить вичерпну інформацію про класи Qt 4, а також дано практичні рекомендації їх застосування, проілюстровані на великій кількості докладно прокоментованих прикладів.", "Макс Шлєє", 893, 2012, "/images/cde95639-3390-44a8-9dcf-20efe30b84fd.jpg", 0,  0),
    ("Щасливі голі люди", "Щасливі голі люди – тематично об’єднаний цикл оповідань Катерини Бабкіної про щастя або, якщо точніше, про те, що стається з нами перед ним. Це історії про покоління тих українців, хто ще краєм життя застав совок і руїну по ньому, хто виріс і зміцнів як міг, радше не завдяки, а всупереч усьому, що з ними відбувалося; про те, як ці люди живуть тепер і взаємодіють зі світом, в якому – і війна, і любов, і еміграція, і Ханой, і Нью-Йорк, і мертві, і живі, і сліпі, і немудрі. І головне – про те, як з цим усім бути щасливим.", "Катерина Бабкіна", 80, 2016, "/images/dcfbbff3-6f56-45e4-af3c-44eb81782749.jpg", 0,  0),
    ("SCALA для нетерпеливых", "Написана для досвідчених програмістів на Java, C ++ і C #, які бажають освоїти функціональне програмування або мову Scala, книга в стислій формі знайомить з основними ідеями і прийомами програмування, що дозволяє швидко освоювати їх і застосовувати на практиці. Це введення в мову особливо добре підходить для нетерплячих користувачів, що бажають вивчати основи Scala і тут же втілювати нові знання в код.", "Кей Хорстман", 407, 2013, "/images/b1b31bcc-facc-4486-878e-ff9b6f7b670a.jpg", 0,  0),
    ("HTML5 и CSS3 для всех", "Перед вами посібник з HTML5 та CSS3, який буде корисним для всіх, від початківця до фахівця. Ви зможете освоїти весь спектр можливостей, пропонованих HTML5 та CSS3. Приклади з книги дозволяють стати професіоналом в області створення повноцінних веб-додатків.", "Алексіс Голдстайн, Луїс Лазаріс, Естель Уейл", 368, 2012, "/images/c50e1d33-201f-4b6a-8270-ebf179346fed.jpg", 0,  0),
    ("Dart в действии", "Dart - мова програмування для розробки веб-додатків, створений компанією Google. Вона володіє сучасними об'єктно-орієнтованими засобами, як Java або C #, не відмовляючись при цьому від властивих JavaScript динамічності і орієнтованості на функціональне програмування. Написані на Dart додатки транслюються в JavaScript, але можуть виконуватися і безпосередньо в браузерах, що підтримують Dart.", "Кріс Бакетт", 526, 2013, "/images/89e8b277-0038-426a-b2d2-29fbc9fa83b0.jpg", 0,  0),
    ("Людина в пошуках справжнього сенсу. Психолог у концтаборі.", "Віктор Франкл – всесвітньо відомий психіатр, психотерапевт, філософ. 1942 року він потрапив до концтабору, де на нього чекали голод, приниження, хвороби, постійна загроза життю. Аналізуючи свою поведінку та поведінку інших в’язнів, Франкл віднайшов стратегії, що утримують людину над прірвою, захищають розум від божевілля та надають сенс життю. Свій жахливий досвід виживання він описав у книжці, яка допомогла мільйонам людей віднайти себе та змінити життя. Віктор Франкл доводить, що тільки-но людина знаходить сенс свого існування, вона отримує сили, щоб здолати будь-які випробування. Поради людини, яка зазнала нелюдських випробувань, варті того, щоб бути почутими.", "Віктор Франкл", 160, 2004, "/images/9a1a1715-26b0-435f-987e-ee5b04236312.jpg", 0,  0),
    ("Країна розваг", "Студент Девін влаштовується на літню роботу в парк атракціонів, сподіваючись забути дівчину, яка розбила йому серце. Але тут відбувається щось моторошне. Кажуть, що в парку бачать примару дівчини, вбитої у Домі жахів. Безжальний маніяк уже забрав життя дівчат, а його так і не знайшли.", "Стівен Кінг", 317, 2015, "/images/1c1a26ab-82d1-43da-bef6-e1cb09557a00.jpg", 0,  0),
    ("Стартап на мільйон. Як українці заробляють статки на технологіях", "Стартап на мільйон — збірка із чотирнадцяти історій успішного українського IT-бізнесу. Ця книга для тих, хто зачитується історіями Говарда Шульца, Ілона Маска, Філа Найта або Стіва Джобса, та хоче дізнатися більше про розвиток бізнесу своєї країни — бізнесу, яким можна пишатися.", "Тімур Ворона", 224, 2017, "/images/af890538-1a29-4789-b7e7-9e873ec5add9.jpg", 0,  0),
    ("Оптимизация и продвижение сайтов в поисковых системах", "Про що мріє власник будь-якого сайту, блогу чи пабліку? Залучити якомога більше зацікавлених відвідувачів, перетворити їх на лояльних клієнтів і при цьому не розоритися на рекламі. У цій книзі зібрані і проаналізовані принципи і методи просування, що дозволяють розібратися, як працюють сучасні пошукові системи, як користувачі шукають інформацію, тобто все те, що допоможе вам привести користувачів на ваш сайт і змусить купувати у вас. Детально обговорюються сучасні способи мережної реклами і нестандартні методи просування, даються конкретні рекомендації щодо оптимізації сайтів різної тематики. Викладені в книзі принципи і рекомендації не втратять своєї актуальності ще довгий час!", "І. Ашманов, А. Іванов", 400, 2009, "/images/c64c3eb1-767a-4615-972e-86530c2e4066.jpg", 0,  0),
    ("Доки світло не згасне назавжди", "Ніякі жертви не варті спроб виправити минуле, адже безжальний світ завжди знайде спосіб примусити за них розплачуватися. Сестри Рута й Індія, попри незвичайні імена, проживають цілком буденні життя: Індія навчається на лікаря, Рута закінчує одинадцятий клас. Усе раптово змінюється, коли через нещасний випадок гине наречений Індії. Раніше близькі сестри віддаляються, звинувачуючи одна одну в тому, що сталося. І саме тоді, коли, здавалося б, із конфлікту вже не виплутатися, Рута виявляє, що здатна впливати на реальність у снах. Невдовзі в її сновидіннях з'являються химерні тіні, і що ближче вони підступають, то дужче мерхне світло довкола дівчини. За нестримним бажанням повернути колишнє життя Рута не помічає, як кожен наступний сон стає дедалі темнішим.", "Макс Кідрук", 560, 2019, "/images/9e9dc588-1379-439a-a65b-8b4494404dfe.jpg", 0,  0),
    ("Java руководство для начинающих", "Чергове видання бестселера, оновлене з урахуванням всіх новинок Java Platform, Standard Edition 9 (Java SE 9), дозволить читачам в найкоротші терміни почати програмувати на мові Java. Досвідчений автор Герберт Шілдт вже на початку книги познайомить читачів з тим, як створюються, компілюються і виконуються програми, написані на Java. Далі обговорюються ключові слова, синтаксис і мовні конструкції, що становлять основу Java. Також будуть розглянуті теми підвищеної складності, включаючи багатопотокове програмування, узагальнення, лямбда-вирази, Swing, JavaFX і ключове нововведення Java SE 9 — модулі. Як бонус читачів чекає знайомство з JShell — нової інтерактивної оболонкою Java.", "Герберт Шілдт", 624, 2014, "/images/07cd4bf9-99f2-451e-9bcb-bc0723735631.jpg", 0,  0),
    ("100 мест всемирного наследия ЮНЕСКО", "Головне завдання Списку Всесвітньої спадщини ЮНЕСКО — охорона і популяризація унікальних пам'яток культури і природи нашої планети. Формуючи список, громадська організація прагне зайвий раз нагадати про те, що для їх збереження потрібно пильна увага з боку громадськості. До Списку Всесвітньої спадщини ЮНЕСКО включено всі культурні, історичні, природні та змішані пам'ятки планети — 981 об'єкти з 159 країн світу.", "Утко Єлизавета Володимирівна", 96, 2014, "/images/f5e22e02-b7b3-4142-b956-ea8a35e9809f.jpg", 0,  0),
    ("Доктор Хаус. Офіційний гід по серіалу", "Ось уже стільки часу мільйони глядачів по всьому світу захоплено стежать за історією мізантропічного геніального доктора на ім'я Грегорі Хаус і його команди фахівців. Щотижня група лікарів на чолі з доктором Хаусом розкриває медичні загадки, які поставили б у безвихідь найкращі уми медицини. Головний герой — персонаж, який неперевершений своєю суперечливістю — він грубий, безтактний, часом абсолютно неетичний по відношенню до пацієнтів, але при цьому настільки привабливий для глядачів серіалу.", "Джекмен Я.", 352, 2010, "/images/ff416e8b-2ae6-452d-9f63-17712db821ce.jpg", 0,  0),
    ("Зовсім не страшна книга про життя, смерть і все, що поміж ними", "Майже кожна родина колись стикається з важкою хворобою, невиліковним діагнозом або реанімацією. Зовсім не страшна книга — про життя після таких новин. Вона складається з порад і справжніх історій з життя й показує, як може бути і як з цим впоратися. Книга розповідає, як прожити погані новини, як спілкуватися з хворою людиною, як організувати догляд вдома і що робити, якщо смерть станеться вдома або в реанімації, як знеболювати, як допомогти дітям пережити хворобу або смерть близької людини, як піклуватися про себе, коли хворіють близькі.", "Анастасія Леухіна", 368, 2020, "/images/41c92640-bb0d-4d4b-b1a4-efe4dbe8ee41.jpg", 0,  0),
    ("Это моя работа. Любовь, жизнь и война сквозь объектив фотокамеры", "Лінсі Аддаріо - одна з небагатьох жінок, хто не боїться працювати військовим журналістом. У 23 роки американка Лінсі Аддаріо вирушила знімати на Кубу, яка фактично не приймала американських журналістів. Гроші, які її батьки виділили їй на весілля, вона вклала в фототехніку і відправилася на війну працювати військовим фотографом. Поки її сестри виходили заміж і народжували дітей, вона працювала в гарячих точках: Афганістані, Пакистані та на кордоні з Сирією. Її двічі викрадали.", "Лінсі Аддаріо", 416, 2018, "/images/e193cdd8-c57d-4535-b48c-cf43877d54c6.jpg", 0,  0),
    ("Про що я говорю, коли говорю про біг", "Збірка автобіографічних нарисів Про що я говорю, коли говорю про біг Харукі Муракамі вважається найвідомішою у світі книгою про біг. Водночас це – розповідь про рух автора по життю: від бару до письма, від письма до ультрамарафону. Адже подорожувати можна і континентами, і мовами, і заняттями. Нон-фікшн у виконанні Муракамі легкий, іронічний, детальний.", "Харукі Муракамі", 160, 2020, "/images/9ddf486a-f0ff-431b-b2b7-5c3bb60ec4bb.jpg", 0,  0),
    ("#невимушені антологія", "Збірка оповідань п’яти молодих, але вже відомих українських авторів покоління міленіум про головні цінності України.", "Катерина Бабкіна, Мирослав Лаюк та ін.", 192, 2016, "/images/d82e0b52-e1d7-4e80-bd46-aa356a008cc4.jpg", 0,  0),
    ("Век волков", "Анджей Сапковський, Генрі Лайон Олді, Марина і Сергій Дяченко, Святослав Логінов, Кір Буличов та інші фантасти діляться з вами своїми зачаровуючими історіями про занепалих ангелів і вампірів, драконів і відьмаків, відьом і ельфів, древніх богів і істот, у яких немає імені. Це розповіді про доблесті і розпачі, про межі між світами, яка іноді стає занадто тонкою...", "А.Саповскій, Г.Л. Олді та ін.", 576, 2015, "/images/eeb7babc-81c6-4ea3-ad83-fb765656d5b7.jpg", 0,  0),
    ("Обережно, тригери!", "Чи вігадані історії небезпечні?", "Ніл Гейман", 368, 2017, "/images/e2968142-8b96-496a-996d-8574a9a3d46c.jpg", 0,  0),
    ("Методы и алгоритмы компьютерной графики в примерах на Visual C++", "У книзі послідовно, 'від простого до складного', розглядаються поняття, алгоритми і методи комп'ютерної графіки, а також засоби програмування. Описано особливості платформ Windows і .NET, розробка програм в середовищі Visual C++ з використанням об'єктно-орієнтованого стилю програмування, можливості, що надаються бібліотекою MFC і архітектурою Document-View, створення багатопоточних додатків з MDI-інтерфейсом. Детально аналізуються можливості бібліотеки нового покоління GDI+: малювання векторних примітивів складної форми з градієнтною заливкою, управління прозорістю векторних і растрових об'єктів, підтримка форматів графічних файлів (BMP, GIF, TIFF, JPEG та ін.).", "Олексій Поляков", 416, 2002, "/images/968d563a-84c0-4463-a28d-3c1801b27762.jpg", 0,  0),
    ("Тінь вітру", "Барселона, 1945 рік. Місто поступово оговтується після війни разом з його втомленими жителями. Серед них — Даніель, син продавця книг. Одного разу його батько показує йому загадкове місце під назвою Цвинтар забутих книг, де хлопець знаходить видання якогось Джуліана Каракса. Але спроби знайти інші книги автора закінчуються безрезультатно — здається, ніби хтось цілеспрямовано знищує все, що написав цей письменник. Намагаючись докопатися до істини, Даніель потрапляє у вир фантазій і пристрастей, любовних інтриг і військових драм. Так на перший погляд непоказна книжка назавжди змінила життя молодої людини", "Карлос Руїс Сафон", 480, 2007, "/images/ab51d13b-e72a-41ce-827a-bfbc915644da.jpg", 0,  0),
    ("Викинги. Ирландская сага", "Вікінг завжди пам'ятає, що його доля в руках богів. Торгрім Нічний Вовк втратив свій корабель, а Арнбьерн успішно грабує ірландські міста. Один мріє повернутися додому, а інший - примножити багатство. І Торгріму доводиться вступити на службу до Анбьерну, щоб повернутися на батьківщину. Але Харальд не поділяє мрії батька.", "Джеймс Л. Нельсон.", 326, 2014, "/images/48126f11-dcc8-4529-9fbb-d0264d05cb16.jpg", 0,  0),
    ("DirectX. Графіка в проєктах Delphi", "Книга присвячена використанню модулів DirectX в додатках, що розробляються в Delphi. Починаючи з простих прикладів, послідовно і детально розглядається створення об'єктів двовимірної і тривимірної графіки, візуальні і кольорові ефекти, а також обговорюються додаткові теми, такі як швидка робота з пристроями введення. Велика увага приділяється питанням оптимізації та прискорення роботи додатків.", "Михайло Краснов", 416, 2002, "/images/c94f4489-01f1-4f58-a6f4-d8c173897fa8.jpg", 0,  0),
    ("Бегство от безопасности", "Коли тримаєшся за півмилі над землею на нейлоновому крилі і надії на висхідний потік повітря, життя висить на чесному слові. Чесне слово Річард Бах дав п'ятдесят років тому — переляканому дитині, яким він був в той час, пообіцявши повернутися до нього і передати все, чого сам навчиться від життя. Обіцянка залишалася невиконаною до тих пір, поки в один прекрасний день, хлопче між небом і землею, Річард не зустрівся з дев'ятирічним Діккі Бахом — невтомним опонентом всіх своїх уявлень...", "Річард Бах", 254, 2001, "/images/71c12b70-c178-4958-b6ee-b6de9ac1623e.jpg", 0,  0),
    ("100 ключових подій в історії України", "Історія України нагадує напівневизначену карту. Це не просто випадковість, а результат обдуманої державної політики. Сотні різних людей, які обслуговують свої країни-матері, багато століть працювали над цими порожніми місцями. Ті, хто створює міфи та фейкові новини, доклали максимум зусиль, щоб наша історія, як і сама Україна, дивилися в очі нащадкам способом, яким віддають перевагу ті, хто намагався асимілювати спадщину Київської Русі.", "Юрій Сорока", 205, 2018, "/images/92132a38-2dcf-41f3-9d73-cb1278e5ece5.jpg", 0,  0),
    ("Щорічник фінського дизайну", "Щорічник фінського дизайну — це міжнародний огляд сучасного фінського дизайну, виданий Design Forum Finland. Це довідник з сучасного фінського дизайну та іскра для дискусій. Як і у попередніх виданнях, Щорічник був розроблений для міжнародного розповсюдження у книгарнях, Інтернет-магазинах, на ярмарках дизайну та виставках, заходах та пресі. Попередні видання поширили повідомлення фінського дизайну в більш ніж 40 країнах.", "Ліза Йокінен та інші", 172, 2015, "/images/cc9a18e6-ec2c-4e70-9df7-e5870b949203.jpg", 0,  0),
    ("Финансы", "Ця книга є базовим підручником з курсу фінансів, який вивчається на першому курсі інституту при підготовці фахівців за програмою MBA. У книзі розглядаються питання, що стосуються всіх аспектів сучасної фінансової науки. Автори книги, — університетські професори Цві Боді і Роберт Мертон (Нобелівський лауреат з економіки 1997 г.), — детально проаналізували проблеми, з якими всі ми стикаємося вдома і на роботі. Виклад традиційних питань корпоративних фінансів спирається на всебічний аналіз їх концептуальних основ: гроші і час; оцінка активів і управління ризиком.", "Зві Боді, Роберт К.Мертон", 592, 2004, "/images/1a61bad9-6ca2-4eea-8bd6-9f7fa2487f75.jpg", 0,  0),
    ("Дао Вінні Пуха. Принципи даосизму на прикладі найвідомішого у світі ведмедя", "Дао і хто? Вінні-Пух і що? Дао Вінні-Пуха! Вчинки Вінні-Пуха мають свою неповторну логіку — і саме завдяки цьому кумедного ведмедика щиро люблять діти й дорослі в усьому світі. У цій книзі Бенджамін Гофф демонструє, як Шлях Пуха перегукується з основними принципами традиційного китайського вчення — даосизму. Доки Іа скаржиться... Паць вагається... а Сова мудрує... Пух просто живе. Дотепна і витончена книжка американського письменника Бенджаміна Гоффа повертає нас у дитинство і водночас занурює у джерело мудрості Дао. Автор вважає природну щирість, бездоганний світогляд та інстинктивні методи вирішення проблем Вінні-Пуха наочним прикладом даоської філософії. Що таке даосизм? Насправді все дуже просто. Ця філософія закликає до життя без упереджених уявлень про те, яким воно має бути. Та якщо вам хочеться дізнатися подробиці, краще все-таки прочитати цю книжку та прислухатися до найвідомішого у світі ведмедика.", "Бенджамін Гофф", 168, 2018, "/images/2498036e-cc42-4a66-9271-dddc24a4bf3f.jpg", 0,  0),
    ("Программирование для Microsoft Windows8. Разработка приложений для Windows Store на С# и XAML", "Шосте видання цієї легендарної книги довелося чекати майже 15 років! У своїй новій праці Чарльз Петцольд, відомий автор і один з піонерів Windows-програмування, розповідає про розробку додатків для Windows Store з використанням C# і XAML в середовищі Windows Runtime.", "Чарльз Петцольд", 1008, 2014, "/images/8b1bab3f-41aa-4f9f-9ab0-12d62080c741.jpg", 0,  0),
    ("Битва за луну", "Експедиції на Місяць стали одним з найвидатніших досягнень цивілізації в XX столітті. Місяць став першим великим небесним тілом, якого змогла досягти людина. Марс і Венера досі освоюються тільки автоматами і фантастами... Автор цієї книги Антон Первушин розповість вам історію взаємин людини і Місяця у всіх подробицях. Чому герої Жюль Верна вирушили на Місяць в гарматному снаряді? Чи піднімалися в космос ракети Третього Рейху? Як вийшло, що СРСР, так довго і впевнено лідирував в 'космічній гонці', поступився першою експедицею на Місяць американцям? Коли буде побудована перша місячна база?", "Антон Первушин", 711, 2007, "/images/8359cc11-859b-4d83-a48b-46b21c22befa.jpg", 0,  0),
    ("Робота рулить", "Книжка директора Google по роботі з персоналом названа бестселером видань New York Times та Wall Street Journal. Робота рулить також входить до списку 15 кращих бізнес-книг 2015 року за версією Business Insider. Журнал Forbes назвав її однією з найкращих книг для лідерів. Чому так багато людей хоче працювати в Google? Навряд чи їх спокушають тільки безкоштовні обіди. Google став роботодавцем мрії тому, що цінує своїх працівників, вдосконалює умови праці і створює плідне середовище для розквіту ідей. Для ґуґлерів робота не існує окремо від життя. Великі й малі компанії, високотехнологічні і цілком традиційні, можуть з користю для себе переймати цей приклад. Ласло Бок, директор Google по роботі з персоналом, на своєму досвіді показує, як набрати команду найкращих гравців, як зробити роботу ефективнішою, а людей щасливішими.", "Ласло Бок", 424, 2017, "/images/1e82e080-0a31-4ccd-92f5-29ade30ce8f6.jpg", 0,  0),
    ("Erlang", "Ця книга є поглибленим вступом до Erlang, мови програмування, що ідеально підходить для будь-якої ситуації, коли необхідні паралельність, відмовостійкість і швидка реакція. Erlang отримує широке поширення з появою багатоядерних процесорів та їх новим масштабованим підходом до паралельності. За допомогою цього посібника ви дізнаєтеся, як писати складні одночасні програми на Erlang, незалежно від вашого досвіду в програмуванні.", "Чезаріні Франческо, Томпсон Саймон", 474, 2009, "/images/33e86b68-61d7-4c27-a0f3-4c5234484ff6.jpg", 0,  0),
    ("Behind closed doors", "Хороший менеджмент важко побачити. Можна побачити результати чудового менеджменту, але непросто побачити, як менеджери досягають цих результатів. Чудове керівництво відбувається на індивідуальних зустрічах та з іншими менеджерами — там, де все приватно. Важко навчитися управлінню на прикладі, коли ти цього не бачиш.", "Йоганна Ротман, Естер Дербі", 167, 2005, "/images/8da77f05-3469-4309-85ac-81c80ee70c32.jpg", 0,  0),
    ("Чому Азії вдалося", "У чому причина стрімкого економічного злету Китаю? Чому не можна беззастережно довіряти думці неоінституціоналістів? Де золота середина в керуванні економікою держави? На ці та інші питання шукає відповідь репортер Financial Times Джо Стадвелл. Він аналізує три головні складники успішної економіки: сільськогосподарську політику, промислове виробництво і розподіл фінансування. У прицілі авторської уваги Японія, Південна Корея, Тайвань, Індонезія, Малайзія, Таїланд, Філіппіни, В’єтнам і Китай. Аналізуючи бізнес цих країн, автор показує, що спрацювало в Азії, а що ні.", "Джо Стадвелл", 448, 2017, "/images/a780f112-62db-45a5-a33b-54ef54c9a27a.jpg", 0,  0),
    ("Степной волк. Сиддхартха", "Степовий вовк — один з найголовніших романів XX століття, який вперше був опублікований в 1927 році. Це і філософська притча, і глибоке дослідження психології людини, марно намагається знайти і знайти власне я. Це історія любові, яка веде до несподіваної трагічної розв'язки, це і політичний, соціальний роман, в якому герой виступає як запеклий критик існуючого міщанства.", "Герман Гессе", 413, 2008, "/images/7bfacfd0-f366-4d36-bd1b-a8f17b18a4cc.jpg", 0,  0),
    ("Інженерна та комп'ютерна графіка", "Інженерна та комп'ютерна графіка: методологічні вказівки до виконання практичних занять та самостійної роботи студента.", "Г.С. Прокудін, С.Ю.Вольська", 54, 2003, "/images/036016d4-fe1c-42c9-a244-81e4150acaad.jpg", 0,  0),
    ("Зазирни у мої сни", "Очевидно, на світі немає дитини, яка б не бачила моторошних снів. Але що робити, якщо жах зі сновидіння поступово переповзає в реальне життя? У дворічного Тео під час нескладної операції раптово зупинилося серце. Упродовж тридцяти шести секунд малюк тримався на межі між світом мертвих і живих. А тоді повернувся. Спливло два роки, перш ніж Мирон Белінський зрозумів, що його син повернувся не сам. Щось прийшло разом із Тео: учепилося і прослизнуло у свідомість, поки хлопчак перебував по той бік лінії, яку більшість із нас перетинає лиш один раз. У відчайдушних намаганнях урятувати сина Мирон виїжджає до Америки, де найновіші досягнення нейротехнологій дають змогу зазирнути в людський сон. Він не здогадується, що бажання допомогти веде до катастрофи, адже найгірше почнеться, щойно той, хто оселився у снах маленького Тео, збагне, що за ним спостерігають.", "Макс Кідрук", 522, 2016, "/images/0c3a8971-a34a-42b4-8011-81a49459e0bd.jpg", 0,  0),
    ("High performance browser networking", "Наскільки ви готові створювати швидкі та ефективні вебдодатки? Ця красномовна книга містить те, що кожен веб-розробник повинен знати про мережу, від фундаментальних обмежень, що впливають на продуктивність, до основних нововведень для створення ще більш потужних браузерних програм — включаючи вдосконалення HTTP 2.0 та XHR, події, що надсилаються сервером (SSE), WebSocket та WebRTC.", "Ілля Григорик", 382, 2013, "/images/ece797b4-9b45-408c-908c-6fc2a5eb6d79.jpg", 0,  0),
    ("Perl Best Practices", "Автор Perl Best Practices Даміан Конвей пояснює, що правила, конвенції, стандарти та практики не лише допомагають програмістам спілкуватися та координувати між собою роботу, вони також забезпечують надійну основу для роздумів про проблеми та загальну мову для висловлення рішень. Це особливо важливо для Perl, оскільки мова розроблена для того, щоб запропонувати безліч способів виконати одне і те ж завдання, і, отже, вона підтримує багато несумісних діалектів.", "Даміан Конвей", 517, 2005, "/images/fc0aa80b-92b8-40ff-a1a2-0534de19fd76.jpg", 0,  0),
    ("Привид замку. Збірник", "За визначенням Фрейда, моторошне — це коли те, що повинно залишатися таємним, виходить назовні... Від цих історій завмирає серце і холоне кров, їх страшно читати на ніч, але відірватися від них неможливо. Едгар По, Уілкі Коллінз, Вашингтон Ірвінг, Брем Стокер...", "Г. Леру, Б.Стокер, А.К. Дойл, У. Коллінз, та ін.", 813, 2016, "/images/0a47f936-6b1f-49b4-bf02-008bafdeb738.jpg", 0,  0),
    ("Разработка устройств на микроконтроллерах AVR", "Cамоучітель допоможе вам всього за шість кроків пройти шлях від чайника, який вивчає ази цифрової техніки, до цілком готового фахівця, який вміє самостійно розробляти схеми будь-яких пристроїв на мікроконтролерах і складати для них програми. Пізнавши основи цифрової логіки, зрозумієте, як працюють складніші елементи цифрової техніки. Потім освоїте основи мікропроцесорної техніки, зрозумієте, як працює мікропроцесор і мікроконтролер. Дізнаєтеся подробиці внутрішнього устрою, архітектуру і можливості сімейства мікроконтролерів AVR, освоїте основи схемотехніки та конструювання мікроелектронних пристроїв. Навчіться ставити завдання на розробку пристрою і вибирати стратегію її вирішення. Вивчіть відразу дві мови програмування для мікроконтролерів (мова Асемблера і мову СІ), навчитеся транслювати, налагоджувати програми, прошивати їх в пам'ять мікроконтролера. Тепер ви вже самостійно зможете розробити власне Мікроконтроллерні пристрій.", "Бєлов А.В.", 528, 2013, "/images/9be40ee8-e7ee-4b9a-8add-510579140390.jpg", 0,  0),
    ("Пригоди Олівера Твіста", "Основною сюжетною лінією популярного роману англійського письменника Чарльза Діккенса є нелегка доля сироти Олівера Твіста, його життя в робітних будинках та у світі підліткової злочинності. Однак хлопчик робить усе від нього залежне, щоб не переступити межу між добром і злом.", "Чарльз Дікенс", 592, 2016, "/images/36bcc40b-851c-42f1-9ef1-7e73e67a53da.jpg", 0,  0),
    ("Программирование для мобильных устройств на iOS", "Ця книга допоможе вам швидко освоїти програмування під iOS і навчить створювати додатки для пристроїв iPhone, iPad і iPod Touch. Докладно описується процес розробки програми для iOS: від першого запуску Xcode до завантажити програму в магазин App Store. Також ви познайомитеся з мовою Objective-C і основними бібліотеками, необхідними при розробці для iOS. Кожна глава книги являє собою урок, який допоможе швидко освоїти розглянуту тему, з великою кількістю прикладів коду та ілюстрацій. У виданні розглядаються версії iOS 5 і Xcode 4.", "А. Аллан", 410, 2013, "/images/09dea7bb-0b5d-4cfe-82bf-725615498fea.jpg", 0,  0),
    ("Когда я говорил...", "Книга являє собою збірник статей автора, має великий досвід не тільки в науковій сфері, але і в практичній ІТ-діяльності. Незважаючи на те, що збірку склали лише деякі з більш ніж тисячі написаних Олександром в різний час і на різну тематику статей, книга виглядає єдиним організмом і читається буквально на одному диханні. Автор живою мовою пише про реальні речі і життєвих ситуаціях.", "Олександр Краковецький", 132, 2014, "/images/a45360f7-a9ff-44d1-8733-3049c2cb0743.jpg", 0,  0),
    ("Фізика майбутнього", "Кому, як не вченим-фізикам, міркувати про те, що буде являти собою світ в 2100 році? Як одним зусиллям волі будуть працювати комп'ютери, як силою думки людина зможе рухати предмети, як ми будемо підключатися до світового інформаційного поля? Чи це можливо? Виявляється, можливо і не таке. Штучні органи, літають у повітрі автомобілі, неймовірна тривалість життя і молодості — всі ці чудеса не фантастика, а науково обгрунтовані прогнози серйозних вчених, інтерв'ю з якими узагальнив у своїй книзі Митио Каку.", "Мітіо Каку", 584, 2014, "/images/01b211ea-bd90-4b14-aa0c-7279f443a5a9.jpg", 0,  0),
    ("Квадрант денежного потока", "Чи замислювалися ви коли-небудь: в чому різниця між тим, хто працює на компанію, і тим, хто працює на себе? Чому одні інвестори роблять гроші з незначним ризиком, в той час як інші розоряються? Чому більшість найманих службовців переходять з одного місця на інше, в той час як інші кидають роботу і створюють власні бізнес-імперії? Чому в індустріальну еру більшість батьків хотіли, щоб їхні діти стали лікарями, бухгалтерами, адвокатами, а в інформаційному столітті люди цих професій постійно зазнають фінансових труднощів?", "Роберт Т. Кіосакі, Шарон Л. Летчер", 192, 2001, "/images/0e64384e-bfa0-4ade-b072-f58afda2ae89.jpg", 0,  0),
    ("Війна світів. Невидимець", "Війна світів та Невидимець Герберта Уеллса, написані в кінці ХІХ століття, до цього часу залишаються джерелом натхнення для поколінь письменників-фантастів, кіномитців і музикантів, для тих, хто роздумує над трагічністю екзистенційного стану невидимості чи слідкує за пригодами людини-невидимки, хто прогнозує майбутнє цивілізації чи просто мріє про завтрашній день. Ці книжки про беззахисність людства перед невідомими позаземними силами Всесвіту і людини — перед спокусою абсолютної влади, яку може дарувати наука.", "Гербер Уеллс", 320, 2016, "/images/c09f9859-66e1-48c4-a2e8-03d4f38424ec.jpg", 0,  0),
    ("Витончене мистецтво забивати на все", "Ми живемо в культурі, зацикленій на занадто оптимістичних очікуваннях. Із самого дитинства нам нав'язують нереалістичні ідеали, яких ми прагнемо досягти попри все: лише найвищі оцінки, досконала зовнішність, надсучасні ґаджети, найкрутіша робота, тільки екзотичні подорожі й не менш яскраві світлини в соцмережах. Постійно женучись за успіхом і щастям, ми намагаємося уникати будь-якого негативу. Однак негативний досвід, як переконує Марк Менсон, надзвичайно цінний: він звільняє нас від міражу щастя.", "Марк Менсон", 160, 2019, "/images/21689443-4055-4ab4-b143-7e4b2e63b2e8.jpg", 0,  0),
    ("Флешка", "Флешка — найпрозоріша книжка найточнішого Іздрика. Вона є підбіркою есеїв, шкіців та колажів Юрія Іздрика, вперше зібраних під однією обкладинкою (сама обкладинка теж є бездоганним шкіцом і колажем того ж Іздрика).Ця підбірка може сприйнятися як довгоочікувана сатисфакція всіма, кому Іздрик здається цікавим і симпатичним, а його проза надто складною. Всі ці тексти писані а замовлення різних видань, тому авторові йшлося передовсім про те, щоби його думки були зрозумілими.Однак простота викладу ніяк не зашкодила тим якостям його прози, які роблять Іздрика великим письменником, — чутливій спостережливості, парадоксальному мисленню, баченню тенденцій ще у зародку, феноменальному мовотворенню. Реалізм Іздрикової літератури полягає у реалізмі мовних конструкцій. Він розуміє, що проза — це не те, про що говориться, а те, як про щось сказано.", "Іздрик", 148, 2009, "/images/5d3b2e43-8ba8-43f6-abad-763357faff53.jpg", 0,  0),
    ("Як ходити до музею. Поради для справді результативного візиту", "Тридцять дві поради дає нідерландський письменник і арт-консультант Йохан Ідема читачам, спраглим мистецьких вражень. Як найкраще скористатися знаннями музейних доглядачів? Чому ваша дитина — ідеальний гід? Чи справді портрети давніх майстрів — це прообраз сучасних селфі? Відповідаючи на такі несподівані питання, його книжка стає інструкцією з перетворення нудного блукання музейними залами на захопливу подорож, сповнену яскравих емоцій. Щоб отримати незабутній досвід у художньому музеї, на думку автора цієї книжки, потрібно робити багато речей інакше. І він пропонує шляхи для цього — на будь-який смак. Можливо, коли ви її прочитаєте, то побіжите до найближчого музею голяка зі складаним стільцем у руках і почнете обмацувати експонати. А може, на довше затримаєтесь перед картиною, заговорите до незнайомого відвідувача, дослухаєтесь до вражень власної дитини. І так відкриєте нові глибини у творах мистецтва. Чи просто отримаєте насолоду від віртуальної подорожі музеями світу під час читання. У книжці багато світла — як у вікні музею, коли дивишся крізь нього на сонячний день із прохолодної галереї.", "Йохан Ідема", 132, 2019, "/images/03755f3d-7160-43af-83d8-7709a86cc102.jpg", 0,  0),
    ("Искусство креативного мышления", "Застосування креативного мислення в некреативниху областях може забезпечити величезну перевагу. Такі сфери діяльності, як бухгалтерський облік, страхування, фінанси, банківська справа, академічна наука, капітальне будівництво, серійне виробництво або будь-яка інша серйозна галузь, можуть стати ідеальним місцем для ін'єкції креативності. Саме в них всупереч загальноприйнятим уявленням застосування креативного підходу виявляється досить ефективним.", "Род Джадкінс", 432, 2016, "/images/b9b37399-e843-4edb-a008-d86692523439.jpg", 0,  0),
    ("Жінки та чоловіки в енергетичному секторі України", "Дослідження Жінки та чоловіки в енергетичному секторі України було виконано у 2018 — початку 2019 рр. Державною установою Інститут економіки та прогнозування НАН України за підтримки Фонду ім. Г. Бьолля для того, щоб виявити найбільш актуальні гендерні проблеми у сфері працевлаштування в енергетичному секторі України та знайти можливі шляхи їх вирішення для забезпечення сталого і соціально справедливого розвитку української енергетики. Партнерами дослідження виступили ГО Жіночий енергетичний клуб України та Апарат Урядової уповноваженої з питань гендерної політики.", "Олександр Дячук, Юлія Галустян та ін", 92, 2019, "/images/514c0ef6-be8f-4f9f-b642-3c14e2ca4ffc.jpg", 0,  0),
    ("Структура и интерпретация компьютерных программ", "Класика програмування від викладачів MIT. Студенти цього відомого навчального закладу починають своє знайомство з програмуванням саме з цієї книги. Побудова абстракцій за допомогою процедур, елементи програмування, процедури і породжувані ними процеси та інші теми.", "Харольд Алельсон, Джеральд Джей Сассаман", 608, 2014, "/images/c9261c19-1ba0-4626-9479-3267b5a4aa90.jpg", 0,  0),
    ("Привет, меня зовут Лон. Я вам нравлюсь?", "Таїланд — країна посмішок і дешевої любові. Секс-туристи з усього світу приїжджають сюди в пошуках самих юних і беззахисних. Але ніхто не замислюється, що відбувається по той бік задоволення…", "Мансанарес Д., Кент Д.", 368, 2017, "/images/550b54f0-a104-4728-ad39-1b8928477fb4.jpg", 0,  0),
    ("История тела. 2640 фактов", "Чи траплялося так, що у вас виникало питання щодо людського тіла, але ви боялися його ставити? Від верху до низу, зовні і зсередини, праворуч і ліворуч весь наш організм це суцільна загадка. У цій книзі є все, що ви хотіли б знати про своє тіло. І навіть те, про що ви не здогадуєтесь.", "Стівен Джуан", 688, 2014, "/images/00f86729-e5d7-43ce-9de9-c67642a3d23e.jpg", 0,  0),
    ("3ds max 8 для 'Чайників'", "Ця книга присвячена провідному додатку по створенню тривимірної анімації — пакету 3ds max 8. Матеріал, викладений у книзі, допоможе читачеві освоїти базові методики моделювання, створення спецефектів та анімації в 3ds max. Будуть розглянуті такі питання, як створення тривимірних моделей, використання джерел світла і камер, застосування модифікаторів і спецефектів, створення анімації, використання надбудов і ін.", "Шаммс Морте", 368, 2006, "/images/54842967-7fb2-4e08-9311-9e0d18141e62.jpg", 0,  0),
    ("Пирамммида", "МММ... Три чудові, ефемерні метелики в логотипі і рядок з вірша Тарковського, як девіз: З тіні у світло перелітаючи ...", "Сергій Мавроді", 192, 2011, "/images/4b03ab6e-9b18-4080-bfbb-5ab61f60b7d2.jpg", 0,  0),
    ("Голуб Геннадій 2", "Хтось скаже вам, що голуби огидні, бо вони тільки те й роблять, що гидять і порпаються у смітті. А хтось без жодного докору сумління наважиться заявити, що пташки ці не вміють розмовляти. Якщо раптом таке колись станеться, познайомте їх з Геннадієм, він — глибоко інтелігентний голуб, який не гайнує своє коротке пташине життя на приземленні швидкоплинні втіхи. Він міркує про високе, про вічне, про неосяжне…", "KORO", 64, 2020, "/images/96650404-009a-4b57-881f-d8813f198dc2.jpg", 0,  0),
    ("DirectX 9: Уроки програмування на С++", "Розглянуто професійне програмування тривимірної графіки під Windows на мові C++ з використанням технології DirectX 9. Розкрито можливості компонента Direct3D з виведення тривимірної графіки, текстурування об'єктів, роботи з освітленням, вершинними і пиксельними шейдерами і ін. Описано також компоненти DirectInput, DirectMusic і DirectSound. Матеріал викладено у вигляді уроків і допоможе читачеві самостійно вивчити технологію DirectX 9, на основі якої створюються професійні комп'ютерні ігри.", "Станіслав Горнаков", 400, 2004, "/images/ca4d6f8a-edd7-4055-aa32-a345db994cff.jpg", 0,  0),
    ("Python Essential Reference", "Основне посилання на Python — остаточний довідковий посібник мови програмування Python — єдиний авторитетний довідник, який надійно розплутує та пояснює як основну мову Python, так і найважливіші частини бібліотеки Python. Книга, розроблена для професійного програміста, лаконічна, чітка та доступна. Вона також включає детальну інформацію про бібліотеку Python та багато розширених тем, які не доступні ні в офіційній документації Python, ні в будь-якому іншому джерелі посилань.", "Девід М. Бізлі", 625, 2006, "/images/525d134c-557e-4ef3-8264-d9863993554a.jpg", 0,  0),
    ("Bunny Smash", "Навіть не знаємо як пояснити що це за книга ;)", "Хіросі Кашівагі", 200, 2009, "/images/fa90017c-1622-4b6a-98c1-b62b22847726.jpg", 0,  0),
    ("Поучительные забавы или занимательные опыты и фокусы", "Чи вважаєте науку нудною? А даремно...", "Том Тіт", 160, 2013, "/images/3357b2a1-241b-4fd0-9210-8e42653cfd9d.jpg", 0,  0),
    ("Абетка фасилітації і тренерства 'ТОБТО'", "Абетка неформальної освіти, фасилітації і тренерства ТОБТО допоможе зорієнтуватися в різноманітті термінів освітньої сфери, які не завжди вживаються у справжньому значенні або ігноруються через їхню невизначеність та неоднозначність. А детальніший опис деяких підходів стане в пригоді фасилітатор(к)ам — практикам для забезпечення більш ефективного та холістичного навчального процесу. Видано тренерською групою Інша Освіта в рамках проекту Діалог заради змін.", "Інша Освіта", 49, 2018, "/images/5457e63c-fe95-416e-82b2-68c75d0a1e94.jpg", 0,  0),
    ("Микроконтроллеры AVR: от простого к сложному. 2-е издание", "Прочитавши цю книгу, читачі познайомляться з мікроконтролерами сімейства AVR, програмним забезпеченням, необхідним для конструювання і програмування схем на основі цих мікроконтролерів, а й охочі ознайомляться з їх застосуванням на практиці, зібравши котрусь із описаних в книзі практичних конструкцій. Книга може бути корисна для радіоаматорів, студентів, а також усім, хто цікавиться застосуванням мікроконтролерів сімейства AVR.", "Голубцов М.С., Кіріченкова А.В.", 304, 2004, "/images/cf523114-199b-4f43-8be1-5cf9913df5dc.jpg", 0,  0),
    ("Экстремальное программирование", "Ця книга харизматичних лідерів екстремального програмування — про те, як планувати проекти розробки програмного забезпечення за технологією XP. В основному вона призначена керівникам — тим, хто повинен складати план робіт, а потім стежити, щоб він відповідав дійсності. Вона буде корисна і програмістам з замовниками, оскільки це дві основні ролі в процесі планування та розробки програмного забезпечення. Екстремальне програмування або XP, eXtreme Programming — гнучка методологія розробки програмного забезпечення.", "Кент Бек, Мартін Фаулер", 143, 2003, "/images/e17974c7-413d-4fbe-ac11-6e49903b3504.jpg", 0,  0),
    ("Тридцять років у бундестазі", "Американський інститут сучасних німецьких студій (AICGS), афілійований до університету Джона Хопкінса, працює виключно з найактуальнішими питаннями щодо Німеччини та США. Незалежний, безпартійний аналітичний центр із поглибленим експертним досвідом та розумінням обох країн, AICGS співпрацює з лідерами та лідерками державного сектору, розробниками та розробницями політики, лідерами та лідерками корпорацій та іншими науково-дослідними центрами для проведення глибокого аналізу з обох сторін Атлантики. Політики та політикині, керівники та керівниці бізнесу та зацікавлена громадськість користуються цими ідеями для прогнозування тенденцій, зниження ризику та позитивного впливу на формування політики. AICGS пропонує комплексну програму громадських форумів, політичних досліджень, досліджень та стипендій, спрямованих на збагачення політичних, корпоративних та наукових округів, які вона обслуговує.", "Андрей С. Марковіц, Джозеф Клевер", 64, 2020, "/images/85695f46-089a-4adb-bb0f-2e2c0d9dfb4a.jpg", 0,  0),
    ("Грокаем алгоритмы", "Алгоритми — це всього лише покрокові інструкції вирішення завдань, і більшість таких задач вже були кимось вирішені, протестовані і перевірені. Можна, звичайно, зануриться в глибоку філософію геніального Кнута, вивчити багатосторінкові фоліанти з доказами і обгрунтуваннями, але чи хочете ви витрачати на це свій час? Відкрийте чудово ілюстровану книгу і ви відразу зрозумієте, що алгоритми — це просто. А грокати алгоритми — це веселе і захоплююче заняття.", "Бхаргава Адітья", 288, 2017, "/images/16877d93-75c1-4e47-82db-00de085a5dd9.jpg", 0,  0),
    ("Як спроєктувати справедливе місто", "Як спроектувати справедливе місто? розповідає 8 простих та захоплюючих історій різних героїв, які представляють різні групи користувачів нашого населення. Два вигадані архітектори, Софія та Остап, будуть провідниками серед історій дівчинки, батька, літньої жінки, пари, дітей та звичайних приїжджих, які відвідують різні міські зони, щоб нагадати нам повсякденні ситуації, у яких проєктування міста з самого початку є вагомою перешкодою. З іншої сторони, книжка пояснює як уважне проєктування може значно покращити якість міського життя без додаткових витрат.", "фонд Генріха Бьолля", 37, 2020, "/images/5cf35189-9ea5-4a80-b9ad-88a9e3898d4d.jpg", 0,  0),
    ("Flash CS5. Руководство разработчика", "Перед вами — докладний посібник з останньої версії Adobe Flash CS5, черговий бестселер від Стіва Джонсона, автора понад 50 книг комп'ютерної тематики. Ця книга допоможе освоїти популярну мультимедійну платформу, починаючи з азів — установки Flash CS5. У кожному розділі міститься велика кількість ілюстрацій, покрокових інструкцій, практичних прикладів і порад для простоти засвоєння матеріалу і реалізації власних проектів. Книга підійде як починаючим користувачам, так і тим, хто вже має досвід роботи в Adobe Flash.", "Стів Джонсон", 560, 2012, "/images/58255d8f-0f85-40f6-b4ce-d71c7782fd2b.jpg", 0,  0),
    ("Программирование на языке Go", "Перед вами практичний посібник з Go, революційно нової мови програмування з вбудованою підтримкою паралельного програмування, який спрощує розробку програм для багатопроцесорних систем. На даний момент, Go — найграндіозніша з нових мов програмування. Вона спочатку створювалася, щоб допомогти задіяти повну потужність сучасних багатоядерних процесорів. У цьому посібнику першопроходця мови Go Марк Саммерфільд розповідається, яким чином писати програми, в повній мірі застосовуючи його революційні можливості та ідіоми.", "Марк Саммерфільд", 580, 2013, "/images/9b59688e-b705-46c6-9507-ced0fd96c40f.jpg", 0,  0),
    ("Книга о лазерах", "Лазерна техніка — одна з магістральних напрямків сучасної НТР — обіцяє докорінно змінити практично всі сфери нашого життя. Книга знайомить з принципами дії лазерів, з різними видами цих приладів, із застосуванням лазерної техніки в самих різних сферах життя. Для середнього і старшого шкільного віку.", "Сергій Танковский", 112, 1988, "/images/e05a6047-97ff-43ec-8a38-b1957d660a9f.jpg", 0,  0),
    ("Изучаем AJAX", "Книга присвячена технології веб-програмування Ajax, що стоїть на щабель вище базових DHTML і JavaScript. За допомогою Ajax можна створювати інтерактивні веб-додатки, що відрізняються швидкодією і високою продуктивністю. Ця книга відповість на питання, як асинхронні запити використовуються в технології Ajax, і допоможе читачеві вийти на новий рівень у створенні веб-додатків", "Бретт Маклафлін", 443, 2008, "/images/8643ee90-39f9-448a-baee-e3b45ffd5fc4.jpg", 0,  0),
    ("Я пізнаю світ. Екологія", "У цьому томі популярної дитячої енциклопедії Я пізнаю світ йдеться про єдність людини і природи, розповідається про екологічні катастрофи, які сталися і які можуть статися, якщо люди не виправлять своїх помилок. Читачі здійснять заочну подорож у відомі заповідники, дізнаються, що таке ентомопарк, де знаходиться пташиний зоопарк.", "О. Є. Чижевський", 414, 2005, "/images/d6f66c0f-bf8e-4667-a9f6-57c4ccc337eb.jpg", 0,  0),
    ("Об'єктно-орієнтоване програмування в С++. 2-е видання", "Книга Айри Пола, автора багатьох популярних видань по С і C++, не є ні підручником по C++, ні курсом з об'єктно-орієнтованого програмування (ООП) взагалі, хоча може бути корисна і в цих двох якостях. Завдання книги абсолютно конкретна: навчити читача писати на C++ об'єктно-орієнтовані програми.", "Айра Пол", 462, 2003, "/images/aff2278b-a517-45b7-9bbc-8127a53d2e16.jpg", 0,  0),
    ("Стратегії геніїв", "Як стають видатними стратегами? Ґейтс, Ґроув і Джобс були першими бізнес-зірками в сучасному світі технологій. Аналізуючи їхній тридцятирічний досвід управління компаніями сукупною вартістю в 1,5 трильйона доларів — Microsoft, Intel та Apple — автори цієї книжки сформулювали п’ять основних правил, що виявилися спільними в їхньому стратегічному плануванні та дозволили випередити конкурентів, які цими принципами нехтували. Саме ці правила допомогли стільниковому телефону iPhone далеко посунути індустріальних гігантів Nokia та BlackBerry, крихітній компанії Microsoft із Сіетла взяти гору над своїм головним замовником — на той час найдорожчою у світі компанією IBM, а майже збанкрутілій фірмі Intel здолати японців, корейців та європейців у боротьбі за світове лідерство в новій революційній технології — виробництві мікропроцесорів. Ця книжка — путівник найкращими сторінками досвіду стратегічного планування, управління та практичної діяльності, який дозволить будь-кому стати ефективним стратегом, як Ґейтс, Ґроув чи Джобс.", "Майкл А. Кусумано і Девід Б. Йоффі", 272, 2015, "/images/f170cf5f-c48b-498a-8763-ed270798b80c.jpg", 0,  0),
    ("Hadoop", "Готові розблокувати потужність ваших даних? За допомогою цього вичерпного посібника ви дізнаєтесь, як створювати та підтримувати надійні, масштабовані розподілені системи за допомогою Apache Hadoop. Ця книга ідеально підходить для програмістів, які хочуть аналізувати набори даних будь-якого розміру, та для адміністраторів, які хочуть налаштувати та запустити кластери Hadoop. Ви знайдете яскраві кейси, які демонструють, як Hadoop використовується для вирішення конкретних проблем. Це третє видання охоплює нещодавні зміни до Hadoop, включаючи матеріали про новий API MapReduce, а також MapReduce 2 та його більш гнучку модель виконання (YARN).", "Том Уайт", 501, 2009, "/images/b1429b56-c1f2-4d33-b922-7195c0214f2d.jpg", 0,  0),
    ("Стань сильнішим. Усвідомити, перемогти, змінити себе.", "Як прочитати власні емоції, навчитися обговорювати їх із близькими? Як реагувати на критику щодо себе? Як звестися на ноги після поразки для другого шансу? Стань сильнішим — книга про те, як знайти в собі мотивацію та сили пережити життєві негаразди й втрати. Бестселер за версією The New York Times, який вже понад рік залишається однією з найпопулярніших книг з психології за рейтингом Amazon. Техаський гумор і відвертість, що дозволяють Брене достукатися до зневірених сердець, також зробили популярними її публічні виступи: відеодоповідь письменниці про вразливість на щорічній Американській інтелектуальній конференції TED стала п’ятою у світі за популярністю серед виступів президентів та нобелівських лауреатів, зібравши більше ніж 25 мільйонів переглядів.", "Брене Браун", 336, 2015, "/images/fa22d3fb-2055-453f-8521-503c2b1a1f85.jpg", 0,  0),
    ("10 успішних компаній", "Де одні шукають зраду, інші знаходять можливості. Історії героїв книжки 10 успішних компаній. Нова якість підприємництва в Україні чудово ілюструють, як можна побудувати якісний та прибутковий бізнес в наших реаліях, попри війну, спадок совка та інші проблеми. Ми зібрали історії 10 компаній, розпитали керівників та працівників про побудову їхнього бізнесу — від появи ідеї до створення якісного українського продукту.", "Олег Криштопа, Христина Бурдим", 160, 2018, "/images/bc62cd41-0811-4099-85a4-342094da424e.jpg", 0,  0),
    ("Сімейна цінність: спогади мами гея", "Рубі Ременда Свансон народилась і виросла в місті Гумболдті (провінція Саскачеван, Канада), у родині з українським корінням. Закінчила Саскачеванський університет у Саскатуні, від 2008 року – генеральна управителька Девонського ботанічного саду Албертського університету. Після камінґауту свого старшого сина Карла Рубі активно долучилася до боротьби за права ЛҐБТ-людей у Канаді, брала участь у низці адвокаційних кампаній, зокрема за ухвалення закону про одностатеві шлюби, в роботі неурядової організації Батьки, родичі та приятелі ґеїв і лесбійок і едмонтонського Прайд-центру та інших підтримчих груп, упродовж багатьох років читала лекції та вела допомогові семінари для батьків ЛҐБТ-дітей в Албертському університеті. Свій досвід громадської активістки і мами ґея Рубі виклала на сторінках мемуарної книжки A Family Outing (2016). 2004-го і відтак іще двічі 2014 року Рубі побувала в Україні як волонтерка місії уряду Канади зі спостереження за виборами, а 2016 року разом зі своїм молодшим сином, фотографом Полом, відвідала західноукраїнські села, звідки понад століття тому виеміґрували до Канади її прадідусі та прабабусі.", "Рубі Ременда Свасон", 255, 2019, "/images/3b7ed44f-779c-4b2e-9f52-b9ade591df30.jpg", 0,  0),
    ("Останній листок", "Останній листок — одна з найкращих новел у світовій літературі, присвячених темі відданості людини іншій людині, любові до ближнього. Вона відзначається великою увагою до маленької людини, порушує проблеми злиденного життя людей мистецтва, боротьби за виживання, взаємопідтримки і самопожертви. О. Генрі створив чудовий твір, який вже понад століття не лишає байдужим читача і вражає своєю неповторністю.", "О. Генрі", 96, 2019, "/images/7ec6f335-f617-41dd-946d-5f81689c85f4.jpg", 0,  0),
    ("Новые правила деловой переписки", "З даного видання ви дізнаєтеся про те, що прийшов час міняти стандарти ділової переписки, які нескінченно застаріли і ґрунтуються на неприродних шаблонах. Автори стверджують, що необхідно керуватися двома новими правилами: поважати адресата і думати про його інтересах. Якщо складати листи, написані на основі цих простих правил, то ви будете викликати до себе симпатію людей, а ще вони допоможуть вам швидше налагодити відносини. Використовуючи поради представлені в книзі, ви опануєте навиком написання супровідних листів до будь-якого виду робочим відповідей і прохань, а також до бізнес-пропозицій. Автори допоможуть вам писати конкретно, захоплююче і яскраво. Ваші тексти звільняться від зайвих слів, штампова виразів і складних речень. Замість цього вони придбають стислість і змістовність. Якщо раніше у вас виникали складності з написанням подібних листів, то тепер у вас є шанс їх позбутися. Для більш швидкого і зручного засвоєння тексту, всі поради авторів доповнені прикладами і замальовками.", "Максим Ільяхов і Людмила Саричева", 256, 2019, "/images/46a1a5a1-5137-4b75-b77c-36aa1ed7602b.jpg", 0,  0),
    ("Толковый словарь сетевых терминов и аббревиатур", "Цей словник є найбільш повне зібрання термінів і абревіатур, що застосовуються в галузі мережевих технологій. У першій частині зібрані стандартні терміни, широко використовувані в області міжмережевих взаємодій, а в другій — специфічні терміни, характерні тільки для Cisco Systems і Cisco IOS. Словник стане практичним і зручним довідником для всіх фахівців в області мережевих технологій; він буде незамінний як при читанні матеріалів загального характеру, так і при вивченні спеціалізованих технічних керівництв.", "Cisco Systems", 366, 2002, "/images/7e7e29df-3d3c-479a-a43e-c392e10a2106.jpg", 0,  0),
    ("Керівництво по захисту від хакерів", "Книга присвячена опису можливих загроз при роботі в мережевому середовищі і створення надійного й ефективного захисту від атак зловмисників. У ній детально обговорюються різні фактори, що впливають на захист мережі та наведено рекомендації щодо створення всеохоплюючої системи безпеки. Для різних типів вразливостей систем описані застосовувані хакерами програми та інструментальні засоби, стратегія і тактика злому, а також заходи, які можуть бути прийняті для захисту від їх використання. Значну увагу приділено опису систем безпеки Windows NT і Unix і специфічним для цих систем методам вторгнення. Книга містить докладні пояснення, велику кількість прикладів і конкретні рекомендації для зміцнення системи безпеки вузлів мережі. Для фахівців в області захисту інформації та системних адміністраторів.", "Ерік Коул", 634, 2002, "/images/99981efb-7950-4bcb-8459-88fefeb1bfe4.jpg", 0,  0),
    ("Куджо", "Маленька безвідповідальність жінки, що необачно обрала коханця… Маленька безвідповідальність чоловіка, що вчасно не відремонтував авто… Маленька безвідповідальність господарів, що не зробили щеплення псу… А Куджо, добрий слинявій велетень-сенбернар, лише, граючись, погнався за летючою мишею. Мікроскопічний вірус сказу перетворив гіганта на страшного монстра, що в нестямі буде вбивати всіх, хто опиниться на його шляху. Капканом під палючим сонцем стане поламана машина для Донни Трентон і її маленького сина. Хто переможе у двобої скаженої люті тварини і матері, що рятуватиме від смерті своє дитя?", "Стівен Кінг", 352, 2016, "/images/55d280fd-a5e1-49dd-9061-f7ca9a15d82d.jpg", 0,  0),
    ("Oh Dear Silvia", "Усі знають справжню Сільвію, чи не так? Сільвія Шуте лежить у лікарні в комі. Сім'я та друзі збираються біля її ліжка, кожен думає, що знає справжню Сільвію. Але чи знають вони? Бо Сільвія приховує таємницю. Таку, якої вона ніколи не може сказати. І коли її відвідувачі збираються, то правда про Сільвію повільно розкривається.", "Дон Френч", 401, 2013, "/images/3cccdbae-0035-45de-a70e-0545e104a908.jpg", 0,  0),
    ("Computer Science Logic and Structure", "Структура та інтерпретація комп’ютерних програм зробила значний вплив на навчальні програми з інформатики протягом останнього десятиліття. Ця довгоочікувана редакція містить зміни у всьому тексті. У книзі є нові реалізації більшості основних систем програмування, включаючи інтерпретатори та компілятори, і автори внесли багато невеликих змін, що відображають їх досвід викладання курсу в Массачусетському технологічному інституті з часу публікації першого видання.", "Сюзан Бауман та Стівен Мандел", 532, 1993, "/images/176e549e-494a-4a97-b004-ba8c4aa7af2e.jpg", 0,  0),
    ("C++. Энциклопедия пользователя", "Книга C++. Енциклопедія користувача містить огляд актуальних тем, пов'язаних з мовою програмування C++. У книзі розглядаються питання об'єктно-орієнтованого аналізу і проектування, універсальної мови програмування UML і розробки об'єктних моделей. Досить докладно представлені питання реалізації програм, а також стандартна бібліотека шаблонів STL. Особливу увагу приділено обробці даних, живучості об'єктів та шифрування, архітектурі CORBA і моделі СОМ. Доданий CD-ROM містить вихідні тексти прикладів, розглянутих у книзі. Книга розрахована на читачів, які мають початкові знання з програмування на мові C ++.", "Джесс Ліберти", 584, 2000, "/images/ad84730f-7cd1-4283-b081-7f54d406406a.jpg", 0,  0),
    ("Arduino для начинающих волшебников", "Пропонований повний курс познайомить вас з особливостями апаратного забезпечення і програмування мікроконтролера Arduino Uno®. Розглянуто графічні мови програмування Snap! і ArduBlock, текстова мова Wiring і виробничі мови. Крім того, ви навчитеся читати, складати і збирати діючі схеми з електронних компонентів. На закінчення ми розповімо вам, як правильно оформляти інженерні проекти. Матеріал викладається в форматі послідовно збудованих тем, супроводжуваних питаннями, практичними завданнями і проектами. Проектна частина курсу може бути розширена серією книг РОБОФІШКІ. Конструюємо роботів на Arduino® видавництва Лабораторія знань.", "Массімо Банці", 128, 2012, "/images/4e5a7528-159b-4fcc-a1da-232f419c0d37.jpg", 0,  0),
    ("Pink Floyd", "Pink Floyd — одна з найуспішніших і видатних груп всіх часів. Це перший випадок, коли член цієї супергрупи висловив свій особистий погляд на її історію. Беззмінний ударник Флойд Нік Мейсон надає нам можливість зазирнути за лаштунки життя Pink Floyd, її злетів і падінь, починаючи з андеграунду кінця шістдесятих, через безпрецедентний успіх The Dark Side of The Moon, через внутрішні скандали і розбіжності, до фінального возз'єднання. Це добра, правдива і оригінальна історія з перших рук про групу, що стала легендою. Мова цієї книги так само чарівна, як ковток джину або сонечко в теплий літній день.", "Нік Мейсон", 468, 2009, "/images/47cd7287-ec67-4d8e-9db8-a20c552e96e2.jpg", 0,  0),
    ("Розробка веб-додатків з використанням ASP.NET і AJAX", "Написана відомим експертом в області інтернет-розробок Діно Еспозіто, ця книга вчить сучасним методам розробки динамічних веб-додатків на базі концепції AJAX. З її допомогою ви навчитеся планувати, проектувати і створювати інтернет-проекти, використовуючи всі ті багаті можливості, які надає зв'язка ASP.NET і AJAX. Ви дізнаєтеся, яким чином за допомогою AJAX можна оптимізувати складні веб-додатки, як ефективно використовувати шаблони (патерни) проектування для AJAX і застосовувати JavaScript-бібліотеки, наприклад, jQuery, а також здійснювати коректну прив'язку даних на стороні клієнта і багато іншого. Книга призначена для програмістів, які працюють з ASP.NET, які хочуть освоїти сучасні рішення і методи веб-розробки, засновані на концепції AJAX.", "Діно Эспозіто", 397, 2012, "/images/e579a611-5d69-4b35-827a-acaf930e275d.jpg", 0,  0),
    ("Путь торговли", "Торговий шлях — для всіх, хто любить бізнес. Це система поглядів на відносини, які пронизують кожне підприємство і будь-якому ринку. Джерелами Шляхів прийнято вважати східну етику та філософію. Це книга для всіх, хто пов'язаний з бізнесом: керівника, продавця, постачальника...", "Тадао Ямагучі", 139, 2006, "/images/a2fa9491-7673-4c51-aa14-e8ba5371c5f4.jpg", 0,  0),
    ("Qt4.7+. Практическое программирование на С++", "Книга присвячена розробці додатків для Windows і Linux з використанням бібліотеки Qt версій 4.7.x і 4.8. Детально розглянуто програмування тривимірної і інтерактивної графіки, баз даних, багатопоточних додатків, створення власних віджетів, опис принципів роботи з XML, а також використання новітніх підсистем Qt Declarative і Qt3/D. Дано опис класів Qt стосовно вирішення конкретних завдань. Значну увагу приділено основним принципам розробки складних додатків. Розглянуто застосування різних професійних інструментів розробника при роботі з бібліотекою Qt. В архіві розташовані початкові тексти демонстраційних прикладів (понад 40).", "Андрій Боровскій", 496, 2012, "/images/b4b5146a-9b2f-4dea-b8d4-3b8c9114356a.jpg", 0,  0),
    ("Идеальний код", "У цій унікальній книзі найавторитетніші розробники програмного забезпечення діляться досвідом оригінального рішення завдань, які поставали перед ними при реалізації великих IT-проектів. За допомогою цього видання читач отримає можливість опинитися на місці провідних програмістів, побачити на власні очі проблеми, що виникали при реалізації різноманітних проектів, і пройти захоплюючий шлях їх подолання. Авторські статті відібрані Грегом Вілсоном, редактором журналу 'Dr. Dobb's Journal', одного з найавторитетніших IT-видань в світі, а також редактором видавництва O'Reilly Енді Орам. Один лише тільки список авторів робить цю книгу справжнім бестселером - тут ви знайдете матеріали, написані такими визнаними професіоналами, як Чарльз Петцольд, Джон Бентлі, Тім Брей, Брайан Керниган, і ще тридцятьма чотирма експертами в області розробки програмного забезпечення.", "Енді Орам и Грег Уілсон", 624, 2009, "/images/1463c749-d2b3-4d9f-9a16-1b5cb07f8132.jpg", 0,  0),
    ("Розмисли наодинці з собою", "Ми живемо у теперішній миті. Усе інше або було, або невідомо, чи буде. Змінюються епохи, а мудрість лишається. Сенс життя і людське призначення, пошук власного шляху і вірних супутників, щоденне долання перешкод і страху заради омріяної мети — вічні істини, до яких апелює Марк Аврелій. Бо, як казав великий римський імператор і філософ, людина не змінюється: минають епохи, з’являються та зникають країни, а людина залишається такою, якою була колись.", "Марк Аврелій", 207, 2020, "/images/6a6b24c3-9349-4f8a-bba8-db832fcf3d18.jpg", 0,  0),
    ("Я вижу вас насквозь", "Автори з багаторічним стажем вивчення жестів людини розкривають таємниці мови тіла! Співрозмовник не зможе нічого приховати від вас!", "Жозеф Мессінжер", 510, 2014, "/images/3a62b910-836e-49d0-a5b4-f84b21252e3e.jpg", 0,  0),
    ("Джедайські техніки", "Автор простою мовою пояснює, як влаштовано наше мислення і пам'ять; чому ми неекономно витрачаємо ресурси нашого мозку; як їх зберігати, як правильно концентруватися, формулювати завдання і відновлюватися для продуктивної роботи.", "Максим Дорофєєв", 349, 2017, "/images/42e3f47d-f8ad-4069-ab5d-c2a2be5ae620.jpg", 0,  0),
    ("Володар Мух", "Один із кращих романів ХХ століття!", "Вільям Ґолдинґ", 300, 2016, "/images/95305f85-259d-4d12-b62b-3c4d18e74edc.jpg", 0,  0),
    ("Шантарам", "Це заломлююча в художній формі сповідь людини, яка зуміла вибратися з безодні і вціліти, потрапила всі списки бестселерів і заслужила захоплені порівняння з творами кращих письменників Нового часу, від Мелвілла до Гемінґвея. Подібно автору, герой цього роману багато років переховувався від закону. Позбавлений після розлучення з дружиною батьківських прав, він став залежним від наркотиків, зробив ряд пограбувань і був засуджений австралійським судом до дев'ятнадцяти років ув'язнення. Втікши на другий рік з в'язниці суворого режиму, він дістався до Бомбея, де був фальшивомонетником і контрабандистом, торгував зброєю і брав участь у розбірках індійської мафії, а також знайшов своє справжнє кохання, щоб знову втратити, та щоб знову знайти…", "Грегорі Девід Робертс", 859, 2009, "/images/0c023da8-2d33-4ccb-a79c-ca0c70bed62c.jpg", 0,  0),
    ("От нуля к единице", "Книга Від нуля до одиниці присвячена технологіям створення успішного стартапу, що веде до створення потужного монопольного бізнесу. Розуміючи конкуренцію як руйнівну силу, Пітер Тіль пропонує своєму читачеві переконатися в дієвості монополістичних бізнес-стратегій на прикладі досвіду величезної кількості компаній, серед яких Facebook, Microsoft, eBay, Twitter і багато інших. Крім того, книга містить міркування про те, що таке стартапне мислення, і рекомендації щодо формування згуртованої робочої команди.", "Пітер Тіль", 187, 2016, "/images/87f0b7bc-abc3-41bf-9c2c-8e0dd7a57b3b.jpg", 0,  0),
    ("М'ятна казка", "Події книги розгортаються довкола хлопчика, якого віддали до притулку. Він хутко розуміє, що справедливості у світі не існує.У цій казці буде кілька м’ятних капучино, чимало снігових кучугур і пара розбитих сердець.", "Олександр Полярний", 157, 2020, "/images/64a26006-006e-44a4-8dc7-cadeaa16bfc2.jpg", 0,  0),
    ("Соціальне підприємництво: посібник для викладачів курсу", "У посібнику представлені основні підходи до розуміння соціального підприємництва, основні принципи створення і розвитку соціального підприємства, фінансові й організаційні аспекти ведення соціального бізнесу.", "Артем Корнецький, Оксана Бойко, Юлія Клебан та ін.", 160, 2019, "/images/8eab57fe-7098-4523-bd55-639bed6cafb3.jpg", 0,  0),
    ("Криза урбанізму. Чому міста роблять нас нещасними", "Сучасні міста колонізували заможні й успішні люди, залишивши далеко позаду жителів сіл і містечок. Хаби, коворкінги і хайтек-інновації поглинули парки, магазини та кав’ярні. Розбудова міст і міських районів призвела до нестримного збільшення орендних плат і концентрації багатства. У цій книжці економіст і соціолог Річард Флорида розглядає кризу, що спричинила нинішній тип урбанізації.", "Річард Флорида", 320, 2019, "/images/24d229f0-0048-4265-9772-a15f43b8ec49.jpg", 0,  0),
    ("Добавь меня в друзья", "Додай мене в друзі — це книга, наповнена голосами, які знаходяться поруч з нами. Це збірка історій для натхнення, віри в себе, в інших людей і в те, що ми не одні зі своїми думками, переживаннями, почуттями і одкровеннями. Це історії, які нікого не залишать байдужим. Історії, які можуть змінити світ.", "Алла Клименко, Дмитро Дір", 172, 2018, "/images/415418db-6848-43fa-b588-d8378d032369.jpg", 0,  0),
    ("Після третього дзвінка вхід до зали забороняється", "Зібрання найяскравіших зразків малої прози за три десятиліття — від найуспішнішої української авторки. Читач знайде тут як визнані шедеври, перекладені багатьма мовами та поставлені на численних європейських сценах (Інопланетянка, Дівчатка, Казка про калинову сопілку), так і маловідомі юнацькі спроби письменниці в різних прозових жанрах. Завершує книжку нова, щойно дописана повість, у якій драма непорозуміння між матір’ю та дочкою стає своєрідним підсумком історії цілого покоління — покоління відкладеної війни.", "Оксана Забужко", 416, 2017, "/images/9c599af3-34b0-43db-9c78-ea48fd1efaf5.jpg", 0,  0),
    ("Розробляємо ціннісні пропозиції", "У книзі Розробляємо ціннісні пропозиції ви знайдете докладні роз'яснення, як розробляти ціннісні пропозиції й аналізувати їх, не витрачаючи зайвих грошей і часу. Доктор Александр Остервальдер – один з авторів книжки Розробляємо ціннісні пропозиції пропонує покрокові дії для розробки й поліпшення ваших продуктів і послуг.", "Александер Остервальдер", 318, 2018, "/images/a1b47c93-01d4-4cd2-b65d-705cfa41f5e8.jpg", 0,  0),
    ("Чистый код", "Навіть поганий програмний код може працювати. Однак якщо код не є чистим, це завжди буде заважати розвитку проєкту і компанії-розробника, віднімаючи значні ресурси на його підтримку і приборкання.", "Роберт Мартін", 464, 2019, "/images/06ba4551-8c97-426f-a92a-6e05f5e20027.jpg", 0,  0),
    ("Навчитися вчитися. Як запустити свій мозок на повну", "Ви математик чи гуманітарій? Професорка Барбара Оклі переконує, що таких понять не існує. Кожен із нас має внутрішній потенціал, щоб засвоїти будь-які знання. Головне — навчитися вчитися й запустити свій мозок на повну. Навіть більше, одноманітний і цілеспрямований рух до розв’язання проблеми — не завжди найкращий шлях. Інколи варто зробити крок назад, щоб поглянути на завдання з іншого боку, запустити свою креативність, навіть якщо ви працюєте над розв’язанням математичної задачі.", "Барбара Оклі", 272, 2018, "/images/c6011fd3-591f-4cff-9064-697e7fb1999a.jpg", 0,  0),
    ("Тестирование DOT COM", "Цей курс лекцій створений для тих, хто хоче навчитися тестування, отримати роботу тестувальника, зрозуміти, як вести себе в корпоративному оточенні, і домогтися професійного та особистісного зростання. Він буде цікавий і учасникам процесу розробки програмного забезпечення, рекрутерам, людям, пов'язаним з інтернетом або тим, які пишуть про нього, і просто всім бажаючим зрозуміти кухню інтернет-стартапів.", "Савін Роман", 312, 2007, "/images/1bb8c0dc-cb73-40ef-ac30-95411d5fa0b3.jpg", 0,  0),
    ("Весь Шерлок Холмс. Загадка Старка Манро", "Шанувальники творчості Конан-Дойля вже протягом століття сперечаються про те, хто ж з героїв великого письменника був у нього найулюбленішим. І, схоже, Шерлок Холмс здає свої позиції! Адже Старк Монро, головний герой книги 'Загадка Старка Монро', за визнанням самого Конан-Дойля, виявився настільки цікавим персонажем, що заради нього письменник був готовий позбутися Шерлока.", "Артур Конан-Дойль", 288, 2017, "/images/7ac72bf0-1831-4282-bdfc-afe53362b95e.jpg", 0,  0),
    ("Жінки Центральної та Східної Європи у Другій світовій війні: Гендерна специфіка досвіду в часи екстремального насильства", "Висвітлено маловивчені питання участі жінок у бойових діях та особливості перебування у військовому полоні, гендерні виміри жіночого досвіду радянського та національного підпілля, життя на окупованих територіях і примусової праці в Третьому райху, розкрито гендерну специфіку Голокосту, порушено складні питання насильства над жінками та співучасті жінок у насильстві в роки війни, окреслено проблеми повоєнних репрезентацій жіночого досвіду війни. Прагнучи вийти за рамки стереотипного бачення жінки у межах трикутника зрадниця – жертва – героїня, вчені показують складну і суперечливу картину жіночого повсякдення у надзвичайних історичних обставинах. Поставлені завдання вирішуються з міждисциплінарних позицій, шляхом аналізу різноманітних історичних джерел, із застосуванням різних наукових підходів та методів. Збірник також дає уявлення про сучасні феміністські підходи до вивчення теми жінка і війна. Для фахівців у галузі історії, гендерних студій, інших гуманітарних наук, а також усіх, хто цікавиться заявленою проблематикою.", "Г. Грінченко, К. Кобченко, О. Кісь", 335, 2015, "/images/5e4d598d-1834-4690-85b1-55d094fe77da.jpg", 0,  0),
    ("Зміна курсу: альтернативи кліматичній катастрофі. Як збудувати світле майбутнє у світі 1,5°C", "Кліматична криза ставить старе запитання з більшою невідкладністю: якого світу ми прагнемо? Світу, який поважає базові потреби та бажання всіх людей жити добре і у здоровому довкіллі? Світу, де чесні правила забезпечують соціальну справедливість та не дозволяють інтересам окремої людини завдавати шкоди суспільному благу? Світу, де можлива демократична залученість та рівноправна участь усіх членів суспільства? І світу, який пропонує все це нашим дітям та їхнім нащадкам на кожному континенті?", "Сюзанн Ґьотце, Верена Керн, Сандра Кірхнер та ін.", 28, 2016, "/images/488f3dc5-f4af-4b9d-b9c1-9744017b3909.jpg", 0,  0),
    ("Аеропорт", "Генеральний директор Міжнародного аеропорту Лінкольна Мел Бейкерсфелд і не помітив, як увійшов у круте життєве піке. Негаразди вдома підсилилися негараздами на роботі: Боїнг-707 застряг на одній зі злітних смуг. Тим часом Рейс Два Транс Америка починає набирати пасажирів. Пілот навіть не підозрює, що єдиною смугою для посадки є саме заблокована Боїнгом. Але все це абсолютно нічого не важить, коли до аеропорту заходить чоловік, що запланував підірвати себе в одному з рейсів. Він уже поклав вибухівку до своєї валізки, він уже на борту літака…", "Артур Хейли", 544, 2018, "/images/ff87f913-601c-46be-baa8-8c374915984d.jpg", 0,  0),
    ("Пиво і філософія", "Вічні теми та інтереси, пов'язані з пивом, - бадьора насолода, товариськість, заспокоєння, пізнання і духовність - завжди актуальні. Збірка статей науковців і броварів 'Пиво і філософія' ілюструє, що кухоль пива з друзями може бути гранню складного, дивовижного і безмежно чарвного життя розуму. Чи є об'єктивні критерії, які дозволять відокремити чудове пиво від негодящого? Що таке автентичність пива? Яке пиво можна вважати справжнім? Як оцінювати пиво і його смак? Як методи виробництва пива впливають на методи його характеристики і чи слід прагнути його найвищої якості? Як пиво відображено у стародавному епосі і міфах? Як приязнь під час споживання пива пов'язана з тривалою щирою дружбою? Як впливає на броварство несправедливе урядове втручання? Що таке стиль пива? Яка роль пива у житті і мисленні видатних філософів?", "Фріц Олгоф", 260, 2010, "/images/1ad8ab26-82f5-4d7c-ba72-acc2937cd58d.jpg", 0,  0),
    ("Атеїст", "В чому сенс життя? Для чого я живу? Чи є Бог? Якщо Він є, чому цього не можна довести? Якщо Він є, чому в світі стільки страждань? Чи є сенс жити, якщо Бога нема? Напевно кожна доросла людина задавала собі такі або схожі запитання. Головний герой цієї книги відправляється в далеку подорож, щоб знайти відповіді на ці запитання з допомогою свого критичного розуму і знань великого мудреця.", "Андрій Харенко", 225, 2016, "/images/ba44ac5a-5887-47de-b967-814f7a6dc67f.jpg", 0,  0),
    ("Homo Urbanus. Парадокс еволюції", "Йелле Роймер (1953 р. н.) директор природничо-історичного музею в Роттердамі, професор Утрехтського університету, палеонтолог, автор багатьох монографій. У цій книжці Йелле Роймер розмірковує над еволюцією людини, перспективами її розвитку та намагається, наскільки це можливо, спрогнозувати майбутнє людського виду, виходячи з мільйонів років історії життя на Землі. Яке майбутнє чекає на людство? Чи існуватиме наш вид через тисячу, десять тисяч, мільйон років? І якщо існуватиме, то в якому вигляді? Все вказує на те, що людство проживає останні блаженні часи індивідуалізму і попереду на нас чекає злиття в один грандіозний колективний суперорганізм, який мешкатиме в урбаністичних термітниках міст майбутнього, перші паростки яких ми вже сьогодні спостерігаємо на прикладі найбільших мегаполісів світу. Можливість виживання нашого виду лежить у площині перетворень і пристосування до навколишнього середовища, яке все сильніше змінюється під натиском штучної 'другої природи'. Майбутнє належить не Людині розумній, а Людині урбаністичній, так вважає Йелле Роймер і, ознайомившись з його аргументацією, з ним важко не погодитися.", "Йелле Роймер", 132, 2017, "/images/830e0edf-beaa-488c-b5f1-427c4897a2b5.jpg", 0,  0),
    ("Думай медленно... Решай быстро", "Наші дії і вчинки визначені нашими думками. Але чи завжди ми контролюємо наше мислення? Нобелівський лауреат Даніель Канеман пояснює, чому ми часом робимо нераціональні вчинки і як ми приймаємо неправильні рішення. У нас є дві системи мислення. 'Повільне' мислення включається, коли ми вирішуємо завдання або вибираємо товар в магазині. Зазвичай нам здається, що ми впевнено контролюємо ці процеси, але не будемо забувати, що позаду нашої свідомості в фоновому режимі постійно працює 'швидке' мислення - автоматичне, миттєве і неусвідомлюване ...", "Даніел Канеман", 309, 2016, "/images/90bec3fc-7b0f-4927-8320-d2793d930563.jpg", 0,  0),
    ("Антихрупкость", "'Антикрихкість' - книга унікальна: вона розповідає про ключові властивості людей, систем, про властивість, у якої до цих пір не було назви. У світі, де панує невизначеність, не можна бажати більшого, ніж бути антикрихким, тобто вміти при зіткненні з хаосом життя не просто залишатися неушкодженим, але і ставати краще колишнього, еволюціонувати, розвиватися. Талеб формулює прості правила, які дозволяють нам подолати крихкість і діяти так, щоб непередбачувана невизначеність, цей грізний і раптовий Чорний лебідь, не завдав нам шкоди - і більш того, щоб ця рідкісна і сильна птах допомогла нам удосконалюватися", "Нассім Ніколас Талеб", 763, 2015, "/images/31caa4bd-3e2c-4b5d-9a75-049a8147a5bf.jpg", 0,  0),
    ("Enterprise", "Enterprise 3 - третій ступінь курсу англійської мови для учнів середньої школи. Відповідає ступені вивчення Pre-Intermediate B1.", "Jenny Daaley", 184, 2015, "/images/720098e6-3fcc-4520-a1c2-03efdeb9640e.jpg", 0,  0),
    ("Чарівна ти", "Скромна Пенні шокована — вона отримала запрошення на побачення з мільйонером Максвеллом. Він привіз дівчину в розкішні апартаменти і подарував неймовірну насолоду... навіть не доторкнувшись до неї. Пенні дізнається, що на ній тестували нову секс-іграшку...", "Чак Поланік", 285, 2015, "/images/5b2befd5-39eb-4c53-8aec-e457a23ca435.jpg", 0,  0),
    ("Пять пороков команды", "Глава однієї високотехнологічної компанії подав у відставку, оскільки робота компанії при ньому розвалювалася на очах. 'Менеджери досягли досконалості в мистецтві підставляти один одного. Команда втратила дух єдності й товариства, його змінила нудна обязаловка. Будь-яка робота затягувалася, якість падало'. Через деякий час в компанію приходить новий керівник і обстановка ще більше загострюється - Кетрін сповнена рішучості розібратися з проблемами команди менеджерів, які майже привели успішну компанію до краху. Якою ціною, і головне, яким чином їй це в підсумку вдається, і розповідає Патрік Ленсіоні.Чому виникають 'п'ять пороків команди' - взаємна недовіра, невимогливість, безвідповідальність, боязнь конфлікту і байдужість до результатів, як їх діагностувати та що з ними робити? У першій частині книги ці питання вирішує Кетрін зі своєю командою, а в другій автор наводить докладний опис цих 'п'яти пороків команди' і методи їх усунення.", "Патрік Ленсіоні", 187, 2013, "/images/b41e22d0-6c44-49a7-a701-69f2d8031b1c.jpg", 0,  0),
    ("PHP объекты, шаблоны и методики программирования", "Четверте видання книги 'PHP: об'єкти, шаблони та методики програмування' було переглянуто і доповнено новим матеріалом. Книга починається з огляду об'єктноорієнтованих можливостей PHP, в який включені важливі теми, такі як визначення класів, успадкування, інкапсуляція, рефлексія і багато іншого. Цей матеріал закладає основи об'єктноорієнтованого проєктування та програмування на PHP. Ви вивчите також деякі основні принципи проєктування. У цьому виданні книги також описані можливості, що з'явилися в PHP версії 5.4, такі як трейти, додаткові розширення на основі рефлексії, уточнення типів параметрів методів, поліпшена обробка виключень і багато інших дрібних розширень мови.", "Метт Зандстра", 600, 2015, "/images/3dabef24-8f27-43ea-845f-3d763c95414b.jpg", 0,  0),
    ("Ясно, понятно", "Ця книга про правильну і зрозумілою комунікації: як за допомогою слів доносити свої думки і впливати на людей. Максим Ільяхов розвиває тему, порушену в бестселері Пиши, скорочуй. Ви дізнаєтеся, як привернути увагу читачів, як допомогти їм зрозуміти і прийняти точку зору автора, як цікаво і доступно розкрити будь-яку тему, навіть найскладнішу. Ви знайдете в книзі більше 50 інструментів поліпшення тексту. У розділі Контекст автор покаже, як впоратися з упередженістю читачів і налаштувати їх на вірне сприйняття інформації. У розділі Інтерес - як перетворити нудний текст в захоплюючий, не змінюючи його змісту.Розділ Текст присвячений використанню прикладів, антиприкладом, аналогій, метафор, сюжетності і слоганів - тому, що допоможе адресату правильно зрозуміти написане вами. У розділі Подача представлені інструменти миттєвого впливу: як донести головне заголовком, схемою або фотографією. Максим Ільяхов розповідає про створення сильного тексту, підкріплюючи теорію прикладами з власної практики.", "Максим Ільяхов і Людмила Саричева", 447, 2021, "/images/d918cacd-2234-4a59-9408-c87c06768f2d.jpg", 0,  0),
    ("Як хотіти й отримати все", "Коли ми чуємо історію успішної людини, то зазвичай захоплюємося красивою картинкою. Але, аналізуючи далі, думаємо — а чим їй довелося пожертвувати заради свого успіху? Авторка цієї книжки переконана: якщо доводиться чимось жертвувати, значить, щось точно іде не так. Тетяні вдалося побудувати успішну кар’єру керівниці міжнародної компанії. Крім того, вона викладає, виховує двох доньок, заснувала книжковий клуб. Її робота не зупиняється після офіційного закінчення робочого дня, водночас Тетяна ніколи не вимикає дім, коли на роботі. Кар’єра, родина, хобі, інші проєкти — все це можна поєднувати та встигати, якщо прийняти просте правило: балансу не існує, є лише персональний вибір на користь тих чи інших речей, які ви вважаєте сенсом свого життя просто зараз. Ця книжка про те, як відсікти зайве і знайти саме свій шлях. Адже насамперед від нас самих залежить, як ми проживемо власне життя. Це може трохи лякати, оскільки передбачає повну відповідальність за свої дії, але це відправна точка для будь-яких звершень.", "Тетяна Лукинюк, Людмила Колб", 320, 2021, "/images/c83c40cd-58fa-4a2f-a979-6750ce28cad8.jpg", 0,  0),
    ("У дикій глушині", "Захоплива історія юнака Кріса Мак-Кендлесса, що знехтував можливостями блискучої кар’єри та відцурався родини, аби втілити свою заповітну мрію — пожити самітником у глушині Аляски. Джон Кракауер, один із найпопулярніших сучасних американських письменників, емоційно веде нас слідами цієї пригоди, шукаючи відповідь на головне питання: чому за здійснену мрію Крісу Мак-Кендлессу довелося заплатити найвищу ціну? Ця книжка стала світовим бестселером, витримала кілька перевидань, і, врешті, її було успішно екранізовано.", "Джон Кракауер", 275, 2020, "/images/cce52ef4-8a7f-485f-ad54-b0ac8a6d81dc.jpg", 0,  0),
    ("Емоційний Інтелект", "Вплив емоцій на наше життя величезна. Емоції визначають наші відносини з людьми, темперамент і сприйняття світу. Хтось пишається відкритістю, а хтось віддає перевагу ретельно приховувати почуття. Деніел Гоулман, автор книги Емоційний інтелект розповість про EQ, чому так важливо про нього знати і як його використовувати для поліпшення ефективності.", "Деніел Ґоулман", 508, 2020, "/images/8b2b22c0-8cfe-41a9-9d25-63ffd6a7923c.jpg", 0,  0),
    ("Американская фантастика 1 том", "У 1-й том серії книг 'Американська Фантастика' входить 2 роману Рея Бредбері, і добірка оповідань.", "Рей Бредбері", 430, 1992, "/images/8f6b6561-248a-47f7-a11d-4a30b23b4cbf.jpg", 0,  0),
    ("Американская фантастика 4 том", "Перед нами дебютний роман, написаний в далекому 1952 році. Назва роману досить містко показує суть проблеми, яка розкривається при прочитанні. У центрі сюжету соціальний конфлікт між представниками технократії, інженерами, керівниками, вченими та простими роботягами, що залишилися за бортом в нових умовах. У міру стрімкого розвитку технологій через війну, а так само через автоматизації виробництва, прості робочі виявилися витіснені зі своїх робочих місць автоматами. Таким чином, більша частина людей виявилася нікому не потрібною в соціальному середовищі, життя просто втратила сенс.", "Курт Воннегут", 336, 1992, "/images/5e682d88-66bf-49bd-ac99-a4bfbc590f19.jpg", 0,  0),
    ("Американская фантастика 6 том", "Шостий том серії книг 'Американська фантастика' вміщує в себе 3 твори Гаррі Гаррісона - 'Нестримна планета', 'Фантастична сага' і так само інші оповідання", "Гаррі Гарісон", 414, 1992, "/images/9ec29dd3-6bc0-4e30-8166-d1b5a1e3f4df.jpg", 0,  0),
    ("Американская фантастика 9 том", "В 9-й том серії книг 'Американська Фантастика'Входить роман Альфреда Бестера і добірка його оповідань.", "Альфред Бестер", 336, 1992, "/images/06ad401b-f7b5-455e-8f8f-7129752956e0.jpg", 0,  0),
    ("Американская фантастика 11 том", "У 11-й том 'Американської фантастики' входить дві повісті Пола Андерсона з циклу 'Патруль часу'. І також підбірка оповідань Роберта Сілверберг", "Пол Андресон та Роберт Сильверберг", 352, 1992, "/images/bbcbd7ca-5f1e-4dc3-b615-80bf23fcf29c.jpg", 0,  0),
    ("Американская фантастика 12 том", "В 12-й том серії книг 'Американська фантастика' входять науково-фантастичні розповіді Фредеріка Брауна і Вільяма Тенна.", "Фредерік Браун и Уільям Тенн", 336, 1992, "/images/df204983-323b-41e5-93fc-7053222645be.jpg", 0,  0),
    ("Американская фантастика 14 том", "Останній 14-й том 'Американської фантастики' вміщує різні розповіді американських письменників", "Кіт Рід, Деймон Найт, Роберт Артур", 410, 1992, "/images/8b15a79c-a0fb-42fe-ba37-76556d61d165.jpg", 0,  0),
    ("Над пропастью во ржи", "Письменник-класик, письменник-загадка, на піку своєї кар'єри оголосив про відхід з літератури та оселився далеко від мирських спокус в глухий американської провінції. Єдиний роман Селінджера 'Над прірвою в житі' став переломною віхою в історії світової літератури. І назва роману, і ім'я його головного героя Холдена Колфілда зробилися кодовими для багатьох поколінь молодих бунтарів - від бітників і хіпі до сучасних радикальних молодіжних рухів.", "Джером Д. Сэлинджер", 332, 2012, "/images/52f99ef1-f5d8-4410-b810-882cfee8b733.jpg", 0,  0),
    ("Лирики Востока", "У збірнику представлені твори арабських, перських та індійських поетів. Лірики Сходу проголошували право людини на щастя, на свободу від примусу, на справедливий устрій життя. Розкриваючи внутрішні переживання своїх героїв, поети, кожен по-своєму, зображали неповторність і красу земного буття.", "Омар Хаям", 478, 1983, "/images/150f0636-aead-4c9d-9c23-40f32deaf623.jpg", 0,  0),
    ("Хроники Амбера", "Королівство Амбер не знайдеш на жодній карті. Воно таємниче, загадково, але немає світу більш реального, ніж Амбер. Решта світи, включаючи Землю - лише його Тіні. Виняток - Царство Хаосу. Воно й Амбер - як негативний і позитивний полюси світу. Амбер - світ меча, Хаос - світ магії. Вогняний Шлях - символ Амбера. Логрус - символ Царства Хаосу. Але це не просто символи, не просто джерела сили та могутності. Вони - Боги цих світів, на їх протиборстві тримається все суще.Дев'ять дітей загадково зниклого короля Амбера Оберона - потенційні спадкоємці престолу. У їхніх стосунках немає теплих почуттів, немає розуміння і доброти. Вони плетуть інтриги один проти одного, вбивають, зраджують, не відаючи про речі більш глобальних, ніж спрага престолу...", "Роджер Желязни", 351, 1993, "/images/7d225ae3-4249-410f-a4fc-b751e05af09c.jpg", 0,  0),
    ("Гарики из Иерусалима", "Ігор Губерман колись писав науково-популярні книги, а став відомий своїми 'Гаріками'. Зараз живе в Ізраїлі, але часто приїжджає в Росію, і на його вечорах незмінно аншлаг 'Гаріки з Єрусалиму' - чотиривірші не тільки про Ізраїль, а й про Росію, не тільки про євреїв, але і про росіян, це Гаріки - про життя, про дрібниці 'мандруючи по світу, я досить багато подивився - не менше, можливо, ніж Дарвін, бувалий. За книгу цю взявся не поверхневий турист, а справжній і завзятий мандрівник '.", "Ігор Губерман", 509, 1998, "/images/e0ca6db8-d904-4476-b7d4-c9a419797e62.jpg", 0,  0),
    ("Чистая Бредятина", "На Марсі живуть котятка, а на моєму задньому дворі - пташина купальня роботи Рафаеля. У слухавці бібікает не вантажівка 'ФедЕкс', а увімкнений диктофон. Гарнітура Таймс Світла оголошує про брак точок. Винахідник упаковок для компакт-дисків потрапляє в пекло. Якщо незрозуміло, як користуватися кувалдою, запитаєте автора. Якщо не знаєте, чим розважитися, коли відбуваються зміни в пам'яті після п'ятдесяти років, - зверніться до нього ж. Сто найбільших книг, які він прочитав, на деякий час вас займуть.", "Стів Мартін", 189, 2004, "/images/f22d811b-6de0-43c6-a48b-880859a912d9.jpg", 0,  0),
    ("Полковнику никто не пишет", "Полковник відкрив бляшанку і виявив, що кави залишилося не більше чайної ложечки. Він зняв з вогню казанок, виплеснув половину води на долівку і почав шкребти банку, витрушуючи в казанок останні крупинки кави, змішані з пластівцями іржі.", "Габріель Гарсіа Маркес", 430, 1989, "/images/05e79edc-4451-4a38-9671-75918e6f902a.jpg", 0,  0),
    ("Кіровоград", "Фотоальбом показує історію Українського міста Кіровоград 20-го сторіччя.", "Іван Миколайович Корзун", 92, 1983, "/images/500bd4e7-4f96-40cb-945b-2a593635c265.jpg", 0,  0),
    ("Життя Пі", "Життя Пі - роман-притча, який розповідає історію Пі, сина власника одного індійського зоопарку. Волею долі він, разом із сім’єю, змушений емігрувати до Канади. Під час мандрівки їхній корабель зазнає аварії, і хлопець опиняється в рятувальному човні: сам на сам із тигром, гієною, мавпою та зеброю. Що ж чекає на них попереду? Це непроста історія про віру, випробування долі, боротьбу за виживання, пошуки Бога та про найпотаємніші закутки людської душі", "Янн Мартель", 400, 2016, "/images/61777abf-8dac-469b-8206-4030058b494b.jpg", 0,  0),
    ("Ацтек", "Життя ацтеків химерна і загадкова, якщо бачиш її з боку. Культ золота і криваві ритуали, дивні звичаї та особливе бачення світу, населеного жорстокими божествами. Але життя є життя, і якщо ти народився ацтеків, то приймаєш її як єдину, даровану тобі долею. Разом з героєм книги ми пройдемо екзотичними шляхами, побачимо розквіт імперії, захопимося величчю Монтесуми, правителя народу ацтеків, будемо журитися і нагадувати, коли блідолиці воїни в залізних обладунках висадяться зі своїх кораблів і пройдуть з вогнем і мечем по священній землі ацтеків.", "Гери Дженінгс", 1312, 2013, "/images/37c61d42-b9f6-42e0-aeba-67abc8c99077.jpg", 0,  0),
    ("Кради як митець. Креативні «фішки», про які тобі ніхто не розповість", "Остін Клеон – письменник і митець. Його роботи можна знайти на сторінках The Wall Street Journal і The New York Times. Автор відомий у цілому світі завдяки неординарному мисленню, його бестселери перекладені 12 мовами! Остін проводить семінари з творчості в компаніях Google, Pixar, SXSW, TEDx. Одного разу Клеон виступав у Нью-Йоркському університеті та дав студентам 10 порад, пор які сам мріяв за часів, коли тільки починав творчий шлях митця. Текст лекції потрапив до мережі й розповсюдився з величезною швидкістю, тоді автор вирішив написати цю книжку. Вона допоможе реалізувати творчий потенціал і надасть натхнення!", "Остін Клеон", 160, 2012, "/images/a41dde82-1d30-4149-ad64-2fc0d39d624b.jpg", 0,  0),
    ("Великий замесел", "Книжка написана живою мовою й розрахована на широке коло читачів без академічних знань фізики. Це історія Всесвіту, в контексті якої автори спростовують традиційну історію його появи, по-новому осмислюють теорію Великого вибуху та заперечують думку пор те, що Земля - єдина планета, на якій є життя. Що, якщо окрім нашої галактики та всесвіту існує безліч інших унікальних космічних об'єктів?", "Стівен Гокінґ, Леонард Млодінов", 208, 2018, "/images/3d35e649-a355-4806-ae15-fa20f6eee756.jpg", 0,  0),
    ("Шоколадні вірші про кохання", "Яке на смак кохання? Як солодкий полуничний джем? Чи як гострий перець чилі? А може у справжнього кохання смак шоколаду? Адже шоколад буває гірким, солодким і навіть солоним, тож кожен пізнає смак саме свого відчуття. Читаючі вірші цієї збірки, ти зможеш відчути саме таке шоколадне кохання, яким його уявляють письменники.", "Збірка", 58, 2012, "/images/80377da5-5053-4bbc-87ee-a1477551f69c.jpg", 0,  0),
    ("Економіка за 30 секунд", "У книзі наведено огляд і ґенезу основних напрямків і теорій економіки, термінологічно-понятійний апарат цієї науки, а також віхи життя і діяльності найвизначніших діячів, які долучилися до становлення й розвитку цієї науки.", "Дональд Маррон", 160, 2020, "/images/0d5bb7b4-eddd-45bf-a75f-eafed074ec15.jpg", 0,  0),
    ("Создавая инновации", "У книзі представлені найновіші інструменти підприємців, дизайнерів та розробників програмного забезпечення з провідних компаній, застосовуючи які ви зможете створювати креативні команди, тестувати нові ідеї та впроваджувати інновації більш ефективно.", "Натан Ферр, Джефф Даєр, Клейтон М. Крістенсен", 304, 2018, "/images/2d0a61de-fef3-47a4-9fdd-ee450762823f.jpg", 0,  0),
    ("Сердце компании. Почему организационная культура значит больше, чем стратегия или финансы", "Книга переконливо доводить, що корпоративна культура означає набагато більше, ніж стратегія, фінанси та ринки, на яких працює компанія. Автор описує, яка організаційна культура сприяє довгостроковому успіху бізнесу, пояснює, як змінювати сформовані в цій сфері стереотипи. Цей процес вимагає чималих зусиль від власників компанії, але в результаті лідерами стають ті, хто стежить за корпоративним здоров'ям, адже таку унікальну перевагу не можна скопіювати.", "Патрік Ленсіоні", 224, 2013, "/images/ed201130-447e-43c0-956b-d23e8c24756b.jpg", 0,  0),
    ("Обнимите своих сотрудников. Прививка от жестокого менеджмента", "В лексиконі Джека Мітчелла, власника успішної мережі магазинів одягу в США, немає слів бос або підлеглий. Ці поняття просто не уживаються у вільній й щасливій атмосфері його компанії, де кожен співробітник відчуває, що його по-справжньому цінують. Приємні дрібниці, незначні знаки уваги або спільний уїк-енд можуть зробити чимало для успіху компанії.", "Джек Мітчелл", 129, 2013, "/images/4843ad62-ed13-434a-bf37-89b589e2988f.jpg", 0,  0),
    ("Идеальный программист. Как стать профессионалом разработки ПО", "Всіх програмістів, які досягають успіху у світі розробки ПЗ, відрізняє одна спільна ознака: найбільше вони переймаються якістю створюваного програмного забезпечення. Це основа для них, тому що вони професіонали своєї справи.", "Роберт Мартін", 224, 2019, "/images/2328fe30-786d-4325-be2e-52cf5cf9d913.jpg", 0,  0),
    ("Неординарні історії успіху", "Що робить людей неординарними? Чи багато вирішує випадок у нашому житті? Як цілковито реалізувати свої таланти? Скільки годин наполегливої праці необхідно, аби стати кращим із кращих? Шокуючі, вражаючі, неймовірні приклади з життя Біла Ґейтса, одне з найвідоміших досліджень в історії психології – експеримент Терміти Льюїса Термана, біографії зірок канадського хокею та звички селян минулого сторіччя…", "Малколм Гладуелл", 320, 2016, "/images/8b52d0ed-0b9b-4fa5-a87c-7e498358051c.jpg", 0,  0),
    ("Як відвернути кліматичну катастрофу. Де ми зараз і що нам робити далі", "У цій нагальній та авторитетній книжці Білл Ґейтс викладає широкий, практичний і доступний план зменшення викидів вуглекислого газу до нуля. Це той план, який допоможе нам уникнути кліматичної катастрофи.", "Білл Ґейтс", 224, 2021, "/images/e832e9b9-afea-43cd-9a53-97e386e63de3.jpg", 0,  0),
    ("Межі зростання. 30 років потому", "У другій половині ХХ ст. з'явилися свідчення вичерпності глобальних природних ресурсів та обмеженості асимілюючих можливостей планетарної екосистеми. Революційною працею нового бачення стала перша доповідь Римського клубу 'Межі зростання' (1972 р.) Донелли та Денніса Медоузів і Йорґена Рандерса, яка примусила замислитися над фізичними межами економічного зростання людства.", "Донелла Медоуз, Йорґен Рандерс, Денніс Медоуз", 464, 2018, "/images/2215fa80-ce57-4a93-a910-8bb226b40974.jpg", 0,  0),
    ("Гаррі Поттер і прокляте дитя. Частина перша і друга", "На основі нового оригінального сюжету Дж. К. Ролінг, Джона Тіффані та Джека Торна. Нова п'єса Джека Торна. Вперше інсценізована продюсерською компанією “СОНЯ ФРІДМЕН ПРОДАКШЕНС”, Коліном Келлендером та “ГАРРІ ПОТТЕР ТЕАТРІКАЛ ПРОДАКШЕНС”. Офіційний спенарій першої інсценізації у Вест-Енді. Спеціальне репетиційне видання.", "Дж. К. Роллінг, Джон Тіффані та Джек Торн", 350, 2020, "/images/165fded9-423c-414e-9650-77815c51b36f.jpg", 0,  0),
    ("Маленький принц. Листи та публіцистика", "Пілот здійснює вимушену посадку в пустелі Сахара і зустрічає одного незвичайного та загадкового хлопчика — принца з іншої планети — астероїда B-612. Маленький принц розповідає про свої пригоди на Землі, говорить про дивовижну троянду, яку залишив на своїй малій планеті, про своє життя на астероїді, де є три вулкани. Одного дня Маленький принц відлітає, щоб вивчити і пізнати інші місця Всесвіту. Відвідуючи інші астероїди, він зустрічає багато дивних дорослих: короля, якому здається, що він править зірками; честолюбця, який хоче, щоб усі ним захоплювалися; п'яницю, який п'є, щоб забути, що йому соромно пити; ділову людину, яка постійно рахує зірки, бо думає, що володіє багатьма з них і хоче купити ще більше; ліхтарника, який, вірний своєму слову, кожну хвилину запалює і гасить ліхтар; географа, який записує в книгу розповіді мандрівників, але сам ніколи нікуди не подорожує. Саме географ радить Маленькому принцу відвідати Землю. І там, крім пілота, який зазнав аварії літака, головний герой зустрічає теж інших персонажів, спілкуючись з якими, дізнається багато важливих речей.", "Антуан де Сент-Екзюпері", 220, 2019, "/images/2cabcf8f-41e5-4ea4-a8c9-ffd5ea716d48.jpg", 0,  0),
    ("Пролетая над гнездом кукушки", "На́д зозу́линим гніздо́м — роман Кена Кізі. В основу твору ліг авторський досвід роботи у психіатричній лікарні, де проводили ЛСД-сесії та робили досліди із використанням сильнодіючих наркотиків.", "Кен Кізі", 324, 2003, "/images/fe2cb242-9c1a-42e3-82fa-b48c5e74feab.jpg", 0,  0),
    ("Проектирование интеллектуальных датчиков с помощью Microchip dsPIC", "На сторінках цієї книги розкриті способи застосування популярних цифрових контролерів сигналів Microchip dsPIC, в яких обчислювальний потенціал потужних цифрових процесорів сигналів успішно поєднано з можливостями мікроконтролерів PIC. Розглядаються питання не лише програмування, а і проектування електронного обладнання. Таким чином читач отримує повне уявлення про процес створення інтерфейсу для трьох конкретних типів датчиків: температури, тиску/навантаження і витрат. Ця практична, легка для сприйняття книга розкриває реальні проблеми, що виникають в повсякденній роботі розробників, і вказує на рішення, що дозволяють реалізувати всі сильні сторони такого потужного засобу як інтелектуальні датчики.", "Крід Хадлстон", 312, 2008, "/images/dfde2dae-8cf4-4a3a-8872-ace96a4b64de.jpg", 0,  0),
    ("Трудные диалоги. Что и как говорить, когда ставки высоки", "У важких діалогах звичайні методики з перемовин, як правило, не дуже ефективні. Крім того, їх складно застосовувати. Ця книга допоможе вам розвити навики, які дозволять виходити із самих складних ситуацій. З книги ви дізнаєтеся, як підготовитися до складних перемовин; як створити довірчу атмосферу під час бесіди; як вчасно помітити, що розмова виходить з-під контролю; як бути переконливим і коректним. а також багато іншого.", "Паттерсон, Гренні, Макмиллан, Світцлер", 249, 2014, "/images/3ed0d6b1-38da-427b-901a-004537e4032f.jpg", 0,  0),
    ("Пинбол-эффект. От византийских мозаик до транзисторов и другие путешествия во времени.", "Ця книга - про дивовижну і захоплюючу історію науково-технічного прогресу.На великій кількості прикладів Джеймс Бьорк доступно і наочно показує, якою хитромудрою траєкторією часом рухається наука і як відкриття вікової та надвікової давності приводять до самих сучасних винаходів. Від фонтанів Версалю до карбюратора, від прянощів до авіабомби, від леза бритви до квазарів - автор скрупульозно вибудовує захоплюючі ланцюжки і прослідковує взаємозв'язки в часі і просторі між предметами, явищами, подіями і людськими звершеннями. Книга адресована широкому колу читачів, які небайдужі до історії наукового пізнання.", "Джеймс Бьорк", 454, 2012, "/images/6f294461-8fe2-4e64-9e1c-84cbb16ce504.jpg", 0,  0),
    ("Миф о Сизифе", "Органічне поєднання дивовижного літературного таланту і філософської глибини дозволило Камю створити свій високохудожній і високоінтелектуальний стиль, який відрізняє його від всих інших письменників. Це видання об'єднує філософський трактат “Миф про Сізіфа”, написаний в блискучій літературній формі, і повість “Падіння”, в якій автор знову намагається знайти відповіді на 'прокляті' питання буття…", "Альбер Камю", 252, 2007, "/images/34516f43-5fe1-478f-bc7b-5c9563d1e62a.jpg", 0,  0),
    ("In the woods", "Коли йому було дванадцять років, Адам Райан пішов грати в ліс зі своїми двома найкращими друзями. Він більше ніколи їх не бачив. Їхні тіла так і не були знайдені, а самого Адама знайшли притиснутим спиною до дуба; його черевики були наповненені кров’ю. Він не пам’ятав того, що сталося. Двадцять років потому Адам, який взяв собі за псевдонім ім’я Роб, стає детективом поліції Дубліна. Його колеги не знають про його минуле. Він працює в команді з Кессі Меддокс, вони найкращі друзі, а також партнери. Коли на місці археологічних розкопок знаходять тіло молодої дівчини, Роб і Кессі отримують справу. Прибувши на місце злочину, Роб розуміє, що це точне місце його дитячої травми. Вони також знаходять заколку його колишнього друга, яку він впізнає . Чи може бути зв’язок між тим давнім, нерозкритим злочином і цим? Знаючи, що його відсторонять від справи, якщо його минуле буде розкрито, Роб приймає доленосне рішення мовчати. Роб і Кессі розслідують вбивство Кеті Девлін, але обидва сподіваються, що вони також можуть розгадати двадцятирічну таємницю лісу.", "Тана Френч", 596, 2007, "/images/563ba247-4a68-4bd4-9875-4f129c2cfff7.jpg", 0,  0),
    ("Design for hackers", "Багато книжок з дизайну намагаються викладати дизайн за допомогою списків що можна і не можна робити. Але хакери знають, що вам потрібне глибше розуміння чогось, щоб дійсно зробити це добре. Design for Hackers об’єднує дизайн за допомогою реверс-інжинірингу імпресіоністичного живопису, скульптури епохи Відродження, інтерфейсу Mac OS X Aqua, веб-інтерфейсу Twitter та багато іншого. Ви дізнаєтеся про теорію кольору, типографіку, пропорції та принципи дизайну. Ці теоретичні поради поєднуються з конкретними, практичними порадами, такими як пропозиції щодо інструментів колірної схеми та діаграми всіх шрифтів, які вам коли-небудь знадобляться. Незалежно від того, займаєтеся ви дизайном взаємодії, чи дизайном інтерфейсу користувача, чи дизайном користувацького досвіду, мобільним дизайном iOS/Android чи старим добрим веб-дизайном, до кінця книги ви побачите дизайн новими очима.", "Девід Кадаві", 326, 2011, "/images/0cef4faf-96c1-4fc4-9c32-789e6dc91468.jpg", 0,  0),
    ("Язык жизни. Ненасильственное общение", "Метод ненасильницького спілкування (ННС) дійсно покращує життя тисяч людей. Він застосовується і в подружній спальні, і в класній кімнаті, і за круглим столом, і на лінії фронту. Корпорації, організації та уряди, що використовують метод ННС, швидко досягають значного прогресу у вирішенні своїх внутрішніх та зовнішніх проблем. Попри свою революційність, метод ННС є дуже простим, логічним і раціональним. Він ніяк не пов'язаний з релігійно-духовними вченнями й психологічними школами та доступний абсолютно будь-якій людині.", "Маршалл Розенберг", 288, 2019, "/images/4690509e-2ed0-441f-886e-3cdd40298b47.jpg", 0,  0),
    ("The Unquiet. Charlie Parker is back.", "New York Times та автор світових бестселерів Джон Конноллі представляють захопливий триллер, в якому приватний детектив Чарлі Паркер мусить розв’язати заплутану історію, сповнену зрад, таємниць та вбивств.", "Джон Коноллі", 565, 2007, "/images/ef0f4fdd-8d3e-4af2-a678-1f7bfce46ebf.jpg", 0,  0),
    ("#жовті_листочки", "Збірка поезій #жовті_листочки — квінтесенція любові до життя, яким би воно не було, насолоди кожною миттю й усім сущим. Відвертість, автобіографічність, малювання словами емоцій, пейзажів — основні інструменти, які чіпляють.", "Ольга Застеба", 128, 2021, "/images/efde9c0c-8c07-41fc-b811-22557dccd3d2.jpg", 0,  0),
    ("НИ СЫ", "Бути впевненим у своїх силах і можливостях. Саме в тому полягає сенс стародавньої філософії НІ СИ. Якщо хочеш отримати велике, будь готовий пожертвувати малим. Якщо хочеш отримати те, що ніколи не мав — навчися робити те, що ніколи не робив.Джен Сінсеро видав книгу, яку прочитало більше мільйона людей в усьому світі. Вона пройшла кілька перевидань. Це посібник з саморозвитку, де автор дає 25 дієвих рекомендацій, слідуючи яким можна змінити життя. Написаний не в повчальному, тужливі психологічному стилі, а з великою часткою гумору та іронії. Читається легко, і мотивує до дій і рішень, здатних змінити життя.", "Джен Сінсеро", 320, 2020, "/images/55cfc0af-27e5-4ab0-8e64-1b5643959f18.jpg", 0,  0),
    ("Продаючи невидиме", "Ця книжка — результат двадцятип’ятирічного досвіду роботи Беквіта з найвпливовішими та найуспішнішими бізнесменами світу, які продають… повітря. Саме так. Цей посібник — збірка ефективних порад у сфері маркетингу послуг. На прикладі великих компаній Federal Express, Citicorp та численних приватних підприємств автор демонструє різноманітні методики і стратегії успішного продажу невидимого товару. Автор говорить про те, що саме слід покращити в продукції компанії, розкриває основи маркетингу послуг, радить, як вигідно позиціонувати послуги та розуміти клієнта. Цікавий і лаконічний посібник, що містить сотні стратегій для досягнення успіху…", "Гаррі Беквіт", 192, 2018, "/images/a85d9bdd-a782-42fa-8221-4c8daa72b651.jpg", 0,  0),
    ("Эллигент", "Влада захоплює матір Тобіаса, Евелін. Раптом виривається правда. З'ясовується, що місто - це не ідеально-структурне суспільство, а 'реаліті-шоу'. Кукловодами є загадкові люди, які ховається за огорожею. Починається третя частина. Тріс і Тобіас разом з друзями вибираються за межі свого дому. Вони хочуть знати правду і знайти свободу. Але потрапляють пряміком в... Бюро Генетичної безпеки. Їх зустрічають працівники Бюро, оскільки саме вони спостерігають за підопічними з народження. Нова реальність теж 'кусається'. Виявляється, в минулому вибухнула генетична війна за Чистоту. У результаті, світ поділили на 'генетично чисті' і 'генетично пошкоджених' особин. А вчені перетворилися на одержимих монстрів і почали експериментувати з створенням штучних поселень та впроваджувати в них своїх агентів. Головна мета піддослідних - служити розтраченим матеріалом і давати 'генетично чисте' потомство. Трис обурена до глибини душі, Тобіас мечеться в пошуках самого себе і вляпується у чергову переділку. А науковці з Бюро знову незадоволені. Вони хочуть влаштувати 'перезавантаження', тобто стерти пам'ять всіх мешканців міста. Тріс та інші вирішують запобігти катастрофі!. Хто виграє?", "Вероніка Рот", 384, 2015, "/images/da5e4454-dda6-4e4b-b0b6-682506f5ea8e.jpg", 0,  0),
    ("Думай і Багатій", "Вважають, що саме книжка “Думай і багатій” була пердвісником зародженням напряму мотиваційної літератури. У ній уперше постала проблема того, якими якостями має володіти успішна в усьому людина. Обдумавши для себе це питання і зробивши відповідні висновки, Наполеон Гілл зміг потрапити до верхівки найуспішніших людей світу.", "Наполеон Гілл", 263, 2018, "/images/62e309ae-1738-457e-b5e8-8560c5b22092.jpg", 0,  0),
    ("Майбутнє розуму", "У книжчі-бестселері Мічіо Кайку розглядає найцікавіший і найскладніший об’єкт у відомому Всесвіті — людський мозок.", "Мічіо Кайку", 407, 2017, "/images/e64cc1d4-0898-4127-ab09-9f4ce895b43d.jpg", 0,  0),
    ("Американський психопат", "Роман американського письменника Брета Істона Елліса. Оповідь ведеться від імені Патріка Бейтмена, багатого жителя Мангеттена та самопроголошеного маніяка-вбивці. Роман опубліковано 1991 року. Твір викликав чималий резонанс через деталізовані описи сцен сексу та насильства. 2000 року світ побачила однойменна екранізація.", "Брет Істон Елліс", 399, 2016, "/images/026ce618-f28d-46ae-b42b-d7704160574a.jpg", 0,  0),
    ("У таборах високих технологій", "Сувора цензура, репресії і “табори перевиховання”. Ці слова звучать як реалії далекого минулого, але, на жаль, це страшна сучасність. Уже багато років у північно-західному регіоні Китаю понад мільйон уйгурів і людей інших меншин утримують у таборах.", "Даррен Байлер", 143, 2021, "/images/717111eb-6a0b-4cb3-a942-e70d589f0944.jpg", 0,  0),
    ("Витончене мистецтво забивати на все", "Ми живемо в культурі, зацикленій на занадто оптимістичних очікуваннях. Із самого дитинства нам нав'язують нереалістичні ідеали, яких ми прагнемо досягти попри все: лише найвищі оцінки, досконала зовнішність, надсучасні ґаджети, найкрутіша робота, тільки екзотичні подорожі й не менш яскраві світлини в соцмережах. Постійно женучись за успіхом і щастям, ми намагаємося уникати будь-якого негативу. Однак негативний досвід, як переконує Марк Менсон, надзвичайно цінний: він звільняє нас від міражу щастя.", "Марк Менсон", 160, 2019, "/images/f061404f-a62f-4aa0-aa71-2956cec468f0.jpg", 0,  0),
    ("Хочу и буду", "Ця книга про те, як знайти себе серед життєвого хаосу від психолога Михайла Лабковского. У рекомендаціях автор не ховається за обтічними формулюваннями, а чітко називає причини проблем. Після прочитання цієї книги ви зможете знайти себе і зрозуміти, чому у вашому житті щось не ладиться або йде не так, як би вам хотілося. Відмінна риса підходу Лабковского — в конкретиці. Ви отримаєте конкретні поради щодо зміни свого життя. Дізнаєтеся, звідки в нас агресія, невпевненість у собі, у чому коріння психології жертви і невміння постояти за себе.", "Михайло Лабковский", 320, 2016, "/images/bf619d1e-fc44-4ac2-a335-d6d58842ba81.jpg", 0,  0),
    ("Ідеальний керівник. Чому їм не можна стати і що з цього випливає", "Книга найвідомішого у світі фахівця з підвищення ефективності організацій присвячена побитому, але водночас вічно актуальному питанні — як стати ідеальним менеджером. Автор переконує: ідеальним бути просто неможливо, але можна відшліфувати грані своєї особистої ефективності та бути ефективним в одній, максимум — у двох галузях.", "Іцхак Кальдерон Адізес", 262, 2020, "/images/949005d4-a950-40d3-8b03-d6e91abb6e0d.jpg", 0,  0),
    ("Облик книги. Избранные статьи о книжном оформлении и типографике", "Збірник допомагає зрозуміти, чому так багато книги виглядають потворно, і по-справжньому оцінити рідкісні, воістину прекрасно виконані видання. Тому він буде цікавий друкарні, видавцям і дизайнерам, а також всім читачам, люблячим книгу.", "Ян Чіхольд", 228, 2009, "/images/3f4d2938-4e5a-48f2-9fc3-a04c4300d795.jpg", 0,  0),
    ("Сила волі. Шлях до влади над собою", "Мова, література, математика, фізика, хімія. А що було б, якби у школах та університетах нас вчили розвивати силу волі? Наскільки успішнішими ми могли б стати? Келлі Макґоніґал допомогла тисячам людей змінитися: кинути пити чи курити, пробігти марафон, зайнятися спортом, створити власний бізнес, налагодити стосунки в сім’ї тощо. Свою методику вона узагальнила в цій книжці.Авторка з наукового боку показує, як влаштовано наше мислення, чому ми уникаємо змін і сповна не використовуємо власний потенціал. Але, окрім теорії, Макґоніґал подає безліч фактів і цікавих прикладів. Та найбільша перевага книжки — це вправи. Виявляється, завдяки ним мозок можна натренувати, як м’язи.", "Келлі Макґоніґал", 265, 2017, "/images/45aa8680-3b48-44b7-9b5d-1eaf06a32ea9.jpg", 0,  0),
    ("От нуля к единице: Как создать стартап, который изменит будущее", "Як створити компанію з нуля, привести її до успіху, зробивши лідером ринку? Пітер Тиль, підприємець, що створив платіжну систему PayPal, і перший інвестор Facebook, вважає, що основа будь-кого успішного стартапа - унікальний продукт, що дає компанії виграшний статус монополії. Тому одно з важливих умов виживання будь-якого проекту - уміння засновників дивитися на світ по-новому, щоб помітити виграшну ідею, яку ніхто ще не розвинув. Саме ці ідеї, уперше озвучені на лекціях в Стэнфордском університеті, лягли в основу книги Пітера Тиля.На прикладі Facebook, Microsoft, eBay, Twitter, LinkedIn і багатьох інших компаній, а головне - на власному унікальному досвіді роботи в PayPal Пітер пояснює, яку стратегію треба вибрати початкуючому бізнесменові, щоб досягти успіху при створенні власного стартапа.", "Тиль Питер", 191, 2019, "/images/4bb5ab8b-7ec0-4724-ad13-d38a4de5d05c.jpg", 0,  0),
    ("Продається все", "Продається все. Джефф Безос та ера Amazon — не лише історія успіху новаторської компанії, а й свого роду посібник, інструкція для тих, хто хоче створити свій бізнес. Бред Стоун захопливо пише про зародження і розвиток Amazon — від часів створення компанії на Волл-стріт на початку 1990-х і дотепер. Amazon одним із перших побачив необмежені можливості інтернету. Стоун розкриває секрет успіху засновника Amazon Джеффа Безоса, одного із найвидатніших бізнес-лідерів сучасності і невтомного генератора ідей, та в деталях розповідає, як Безос дисципліновано і подекуди педантично втілює новаторські ідеї у життя, ламає стереотипи і не визнає загальноприйнятих правил, запроваджуючи натомість свої.", "Бред Стоун", 400, 2017, "/images/5f714b5e-5e1a-46e9-a318-1a3421bb3e2a.jpg", 0,  0),
    ("Муза и чудовище. Как организовать творческий труд", "Всі пам'ятають казку Красуня і чудовисько. Дівчина, спочатку страшний потворного істоти, виявляла незабаром, що воно не небезпечно, потім - що вельми приємно в спілкуванні, ну а потім вже ставало ясно, що воно і є її щастя. Точно те ж саме можна сказати про відносини творчості і самоорганізації. Ви боїтеся самої думки про те, щоб навести порядок в справах, планах або хоча б у власній кімнаті, виправдовуючись тим, що істинному художнику необхідний творчий безлад і свобода польоту думки? Чи не лукавте. Скільки ідей померло від згадки про те, що для їх фіксації потрібно розчистити краєчок столу або відшукати блокнот! Скільки планів ви не змогли реалізувати лише тому, що не розрахували сил і / або часу! Скільки заважають улюбленій справі проблем виросло з того, що ви рік тому не зробили щось мале, але дуже важливе! Якщо так буде тривати і далі, то в кінці кінців некерована рутина змусить вас підкоритися їй. І болото побуту зійдеться над вашою головою. І це ви вважаєте творчою свободою? Вихід один: упорядкувати своє життя прямо зараз. Ваша муза просто ще не знає, що її щастя в самоорганізації. Цьому покликана допомогти книга, написана талановитим дизайнером і художником, неймовірно працездатною людиною Яною Франк. Тут викладена її особиста система організації творчої праці. Ви знайдете тут приклади її планів і навчитеся будувати аналогічні - зберігаючи при цьому свою індивідуальність. Ця система пройшла перевірку часом і важкими життєвими обставинами. Ми впевнені, що вона буде вам корисна.", "Яна Франк", 272, 2012, "/images/26e9dd74-de64-482d-b50d-827943b92454.jpg", 0,  0),
    ("Почему возникают конфликты?", "Потік новин двадцять чотири години на добу, журналістика, інтернет, подорожі земною кулею… Наш великий світ стає дедалі ближчим і доступнішим, ніж то було колись, - включно з конфліктами й війною. Незалежно від того, чи стосується конфлікт вашої країни, будь-яке протистояння, будь-яка війна певним чином впливає на життя кожного. Така кількість конфліктів у світі примушує замислитися: чому ж вони виникають?", "Нікі Вокер", 80, 2018, "/images/39b19c06-c494-4f35-bee5-0a2e66e673f8.jpg", 0,  0),
    ("Правила Черчилля. Идеи, наблюдения, афоризмы", "У книзі зібрані кращі ідеї та висловлювання великого Черчілля. Її буде цікаво прочитати кожній людині!", "Джеймс Хьюмс", 280, 2013, "/images/86ae3fd3-f766-44ab-9ca2-2a907eb3bc65.jpg", 0,  0),
    ("Из третьего мира — в первый. История Сингапура", "Коли крихітний Сінгапур в 1965 році отримав незалежність, ніхто не вірив, що йому вдасться вижити. Проте він не просто вижив, а перетворився на процвітаючу столицю Азіатського регіону з кращим у світі аеропортом, найбільшою авіалінією, ключовим торговим портом, зайнявши четверте місце у світі по рівню доходу на душу населення. Про це диво у своїх мемуарах розповідає колишній прем'єр-міністр Сінгапуру Ли Куан Ю. Він пожвавлює перед читачами історію, аналізує основні стратегічні рішення сучасності, пише про те, як з року в рік направляв складні стосунки США, Китаю і Тайваню, служачи для керівників цих держав і довіреною особою, і вісником, і експертом. Для кого ця книга Книга буде цікава усім, хто цікавиться економікою і політикою, психологією стосунків. А також для тих, хто не вірить, що відстала держава може швидко стати передовою.", "Лі Куан Ю", 562, 2013, "/images/893318d7-de88-4b76-9661-fb95713445af.jpg", 0,  0),
    ("Думай как математик. Как решать любые проблемы быстрее и эффективнее", "Прийнято вважати, що математики — це люди, наділені неабиякими інтелектуальними здібностями, які потрібно розвивати з самого дитинства. І більшості точність і логічність математичного мислення недоступна. Барбара Оаклі, доктор наук, доводить, що кожен може змінити спосіб свого мислення та оволодіти прийомами, які використовують всі фахівці з точних наук.", "Барбара Оаклі", 283, 2019, "/images/d464060b-a584-4d25-bc2e-243825c3e70b.jpg", 0,  0),
    ("Продажи по-взрослому. 19 инструментов управления", "У великій кількості бізнесів основний канал просування і 90% продажів - це особисті продажу. Додаткові канали - інтернет-просування, директ-маркетинг, спеціальні заходи, виставки - грають супутню роль.У книзі описуються основні інструменти управління відділом продажів, що мають практичний прикладний характер. Кожна глава містить посилання і приклади з практики, які ілюструють застосування інструментів продажів.", "Ася Баришева", 288, 2014, "/images/3b31fea8-7331-4523-be72-55bd4b2de72d.jpg", 0,  0),
    ("Кафе на краю світу", "Зупиніться. Поставте на паузу щоденну метушню, вирвіться з її тенет і зазирніть до кафе на краю світу. Розгорніть меню й придивіться уважніше до останньої сторінки — там ви прочитаєте три запитання, від яких тікали все життя. Чому ви тут? Чи боїтеся ви смерті? Чи вдоволені ви? Ця книжка допоможе у пошуках відповідей. З нею ви запалите вогонь у душі, усвідомите, що цінне, а що — марнота, поглянете на життя під іншим кутом зору, незалежно від набутого досвіду. Кафе на краю світу — справжній видавничий феномен, бестселер поза часом, що надихнув мільйони людей у всьому світі на великі зміни. Чарівна, легка й наснажлива, ця книжка вплине на ваш світогляд і назавжди змінить життя.", "Джон П. Стрелекі", 125, 2023, "/images/df73c802-9316-4233-a61e-52d6054e1480.jpg", 0,  0),
    ("Повернення до кафе на краю світу", "Життя Джона назавжди змінилося після того, як він заблукав і потрапив у маленьке затишне кафе Чому ви тут?. Чоловік провів там усю ніч і полишив це місце з новим поглядом на сенс буття загалом і на своє життя зокрема. Джон дуже здивувався, коли десятьма роками пізніше знову опинився в цьому кафе. От тільки цього разу в меню були три нові запитання. І відповісти на них уже мала Джессіка — випадкова відвідувачка, яка втратила життєвий орієнтир. Час, проведений у кафе, надихає Джона і Джессіку заглибитись у себе, звіритись зі своїми цінностями й жити справжнім та насиченим життям. Події, описані в цій книжці, повертають читачів у те місце, де завжди є смачна їжа і де ставлять запитання, які спонукають замислитися про найважливіше. Тож улаштовуйтеся зручніше й насолоджуйтеся поверненням до кафе на краю світу", "Джон П. Стрелекі", 299, 2022, "/images/8e3e5537-4020-45a9-a9d3-f437c6bd8c53.jpg", 0,  0),
    ("Чудесный нож", "Після подій на Свальбарде Ліра Белаква потрапляє в новий світ, де вона зустрічає Уїлла. Уїллу дванадцять років, і він тільки що скоїв вбивство. Разом вони виявляються в місті покинутих дітей під назвою Читагацце, де на землі вулиці борознять Привиди, які поглинають душі дорослих, але бояться дітей, а небо ділять між собою відьми і білосніжні ангели... Кожен йде своїм шляхом: Ліра шукає значення Пилу, а Уїлл - свого зниклого батька, але вони знаходять предмет могутньої сили - Чудовий ніж, здатний розрізати будь-яку річ і навіть вікна в інші світи...", "Філіп Пулман", 415, 2012, "/images/6fd19df0-1d21-499a-9fb9-13cc2435ba3e.jpg", 0,  0);
